import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";
import axios from "axios";

function MyCourses() {
  const [purchasedCourses, setPurchasedCourses] = useState([]);
  const [freeCourses, setFreeCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [paymentMessage, setPaymentMessage] = useState(null);
  
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);
  
  // Check if user came from payment success
  const paymentSuccess = searchParams.get('payment_success');
  const orderId = searchParams.get('order_id');
  const sessionId = searchParams.get('session_id');

  useEffect(() => {
    fetchUserCourses();
    handlePaymentRedirect();
  }, []);

  const handlePaymentRedirect = async () => {
    if (paymentSuccess === 'true' && (orderId || sessionId)) {
      try {
        // Get the token for authorization
        const token = localStorage.getItem('token');
        
        if (!token) {
          setPaymentMessage({
            type: 'error',
            message: 'Please log in to view your courses after payment.'
          });
          return;
        }

        // Handle checkout success from Stripe - NOW WITH AUTHORIZATION HEADER
        const response = await axios.get(
          `http://localhost:5000/api/orders/checkout-success?session_id=${sessionId}&order_id=${orderId}&payment_success=true`,
          {
            headers: {
              'Authorization': `Bearer ${token}`
            }
          }
        );
        
        if (response.data.success) {
          setPaymentMessage({
            type: 'success',
            message: 'Payment successful! Your courses are now available.'
          });
          
          // Remove payment params from URL
          const newUrl = window.location.pathname;
          window.history.replaceState({}, '', newUrl);
          
          // Refresh courses after successful payment
          setTimeout(() => {
            fetchUserCourses();
          }, 1000);
        }
      } catch (error) {
        console.error('Error handling payment success:', error);
        
        if (error.response?.status === 401) {
          setPaymentMessage({
            type: 'error',
            message: 'Session expired. Please log in again to view your courses.'
          });
        } else {
          setPaymentMessage({
            type: 'error',
            message: 'Payment processed but there was an error loading your courses. Please refresh the page.'
          });
        }
      }
    } else if (searchParams.get('canceled') === 'true') {
      setPaymentMessage({
        type: 'warning',
        message: 'Payment was canceled. Your courses are still in your cart.'
      });
      
      // Remove cancel params from URL
      const newUrl = window.location.pathname;
      window.history.replaceState({}, '', newUrl);
    }
  };

  // Helper function to get a proper thumbnail URL
  const getCourseThumbnail = (course) => {
    if (course.thumbnailUrl) {
      // Clean up the URL - remove extra spaces and validate
      const cleanUrl = course.thumbnailUrl.trim();
      
      // Check if it's a valid HTTP/HTTPS URL
      if (cleanUrl.startsWith('http://') || cleanUrl.startsWith('https://')) {
        return cleanUrl;
      }
      
      // If it's a relative path, make it absolute (adjust the base URL as needed)
      if (cleanUrl.startsWith('/')) {
        return `http://localhost:5000${cleanUrl}`;
      }
      
      // If it's just a filename, assume it's in uploads folder
      if (!cleanUrl.includes('/')) {
        return `http://localhost:5000/uploads/courses/${cleanUrl}`;
      }
    }
    
    // Default fallback - you can replace this with a course-related placeholder
    return '/images/default-course-thumbnail.jpg';
  };

  const fetchUserCourses = async () => {
    try {
      setLoading(true);
      setError(null);
      
      const token = localStorage.getItem('token');
      if (!token) {
        setError('Please log in to view your courses.');
        setLoading(false);
        return;
      }
      
      // Get user's completed orders (purchased courses)
      const ordersResponse = await axios.get('http://localhost:5000/api/orders/user-orders', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      console.log('Orders response:', ordersResponse.data);

      if (ordersResponse.data.orders) {
        // Extract purchased courses from completed orders
        const purchased = [];
        const free = [];

        ordersResponse.data.orders.forEach(order => {
          if (order.payment.status === 'completed' && order.items) {
            order.items.forEach(item => {
              if (item.course) {
                const courseData = {
                  id: item.course._id,
                  title: item.course.title,
                  instructor: item.course.instructor || 'Unknown Instructor',
                  price: item.price,
                  thumbnail: getCourseThumbnail(item.course),
                  enrollmentDate: order.createdAt,
                  progress: 0, // You'll need to implement progress tracking
                  duration: item.course.duration || 'N/A',
                  description: item.course.description || '',
                  orderNumber: order.orderNumber,
                  purchaseDate: order.createdAt,
                  completedAt: order.completedAt,
                  // Additional learning-focused data
                  totalLessons: item.course.totalLessons || 0,
                  completedLessons: 0, // You'll need to implement this
                  lastAccessed: order.lastAccessed || order.createdAt,
                  nextLesson: 'Getting Started', // You'll need to implement this
                  difficulty: item.course.difficulty || 'Beginner'
                };

                if (item.price === 0) {
                  free.push(courseData);
                } else {
                  purchased.push(courseData);
                }
              }
            });
          }
        });

        // Remove duplicates (in case user purchased same course multiple times)
        const uniquePurchased = purchased.filter((course, index, self) =>
          index === self.findIndex(c => c.id === course.id)
        );
        
        const uniqueFree = free.filter((course, index, self) =>
          index === self.findIndex(c => c.id === course.id)
        );

        setPurchasedCourses(uniquePurchased);
        setFreeCourses(uniqueFree);
      }

    } catch (error) {
      console.error('Error fetching user courses:', error);
      setError('Failed to load your courses. Please try again.');
      
      // If token is invalid, might need to redirect to login
      if (error.response?.status === 401) {
        setError('Session expired. Please log in again.');
      }
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  const getTimeAgo = (dateString) => {
    const now = new Date();
    const date = new Date(dateString);
    const diffTime = Math.abs(now - date);
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    
    if (diffDays === 1) return 'Yesterday';
    if (diffDays < 7) return `${diffDays} days ago`;
    if (diffDays < 30) return `${Math.ceil(diffDays / 7)} weeks ago`;
    return `${Math.ceil(diffDays / 30)} months ago`;
  };

  const getProgressColor = (progress) => {
    if (progress === 100) return 'bg-success';
    if (progress >= 70) return 'bg-info';
    if (progress >= 40) return 'bg-warning';
    return 'bg-danger';
  };

  const getDifficultyColor = (difficulty) => {
    switch (difficulty.toLowerCase()) {
      case 'beginner': return 'bg-success';
      case 'intermediate': return 'bg-warning';
      case 'advanced': return 'bg-danger';
      default: return 'bg-secondary';
    }
  };

  const CourseCard = ({ course, isPurchased }) => (
    <div className="col-12 col-lg-3 col-md-4 col-sm-6 mb-5">
      <div className="card h-100 shadow-sm border-0">
        <div className="position-relative">
          <img
            src={course.thumbnail}
            className="card-img-top"
            alt={course.title}
            style={{ height: '230px', objectFit: 'cover' }}
            onError={(e) => {
              console.log('Image failed to load:', course.thumbnail);
              e.target.src = '/images/default-course-thumbnail.jpg';
            }}
          />
          
          {/* Status badges */}
          <div className="position-absolute top-0 end-0 m-2">
            <span className={`badge ${isPurchased ? 'bg-primary' : 'bg-success'} mb-1`}>
              {isPurchased ? 'Purchased' : 'Free'}
            </span>
            <br />
            <span className={`badge ${getDifficultyColor(course.difficulty)} opacity-75`}>
              {course.difficulty}
            </span>
          </div>
          
          {/* Completion badge */}
          {course.progress === 100 && (
            <div className="position-absolute top-0 start-0 m-2">
              <span className="badge bg-success">
                <i className="bi bi-check-circle-fill me-1"></i>
                Completed
              </span>
            </div>
          )}
        </div>
        
        <div className="card-body d-flex flex-column">
          {/* Course Title - EMPHASIZED */}
          <h5 className="card-title fw-bold mb-2" title={course.title}>
            {course.title}
          </h5>
          
          {/* Instructor - EMPHASIZED */}
          <div className="mb-3">
            <span className="text-primary fw-medium">
              <i className="bi bi-person-fill me-1"></i>
              {course.instructor}
            </span>
          </div>
          
          {/* Duration - EMPHASIZED */}
          <div className="mb-3">
            <span className="text-dark fw-medium">
              <i className="bi bi-clock-fill me-1"></i>
              {course.duration}
            </span>
          </div>
          
          {/* Progress Bar - MOST EMPHASIZED */}
          <div className="mb-3">
            <div className="d-flex justify-content-between align-items-center mb-2">
              <span className="fw-semibold text-dark">Progress</span>
              <span className="badge bg-light text-dark fs-6">{course.progress}%</span>
            </div>
            <div className="progress mb-2" style={{ height: '8px' }}>
              <div
                className={`progress-bar ${getProgressColor(course.progress)}`}
                role="progressbar"
                style={{ width: `${course.progress}%` }}
                aria-valuenow={course.progress}
                aria-valuemin="0"
                aria-valuemax="100"
              ></div>
            </div>
            
            {/* Lessons Progress */}
            {course.totalLessons > 0 && (
              <small className="text-muted">
                <i className="bi bi-book me-1"></i>
                {course.completedLessons} of {course.totalLessons} lessons completed
              </small>
            )}
          </div>

          {/* Next Lesson - EMPHASIZED */}
          {course.progress < 100 && (
            <div className="mb-3">
              <small className="text-primary fw-medium">
                <i className="bi bi-play-circle-fill me-1"></i>
                Next: {course.nextLesson}
              </small>
            </div>
          )}

          {/* Last Accessed - EMPHASIZED */}
          <div className="mb-3">
            <small className="text-success fw-medium">
              <i className="bi bi-clock-history me-1"></i>
              Last studied: {getTimeAgo(course.lastAccessed)}
            </small>
          </div>

          {/* DE-EMPHASIZED INFO - Much smaller and lighter */}
          <div className="mt-auto">
            <div className="border-top pt-2 mb-3">
              <div className="row">
                <div className="col-12 mb-1">
                  <small className="text-muted opacity-75" style={{ fontSize: '0.7rem' }}>
                    <i className="bi bi-calendar3 me-1"></i>
                    Enrolled: {formatDate(course.enrollmentDate)}
                  </small>
                </div>
                {isPurchased && (
                  <div className="col-12 mb-1">
                    <small className="text-muted opacity-75" style={{ fontSize: '0.7rem' }}>
                      <i className="bi bi-currency-rupee me-1"></i>
                      ₹{course.price}
                    </small>
                  </div>
                )}
                {course.orderNumber && (
                  <div className="col-12 mb-1">
                    <small className="text-muted opacity-50" style={{ fontSize: '0.65rem' }}>
                      <i className="bi bi-receipt me-1"></i>
                      #{course.orderNumber}
                    </small>
                  </div>
                )}
              </div>
            </div>

            {/* MAIN ACTION BUTTON - MOST EMPHASIZED */}
            <Link
              to={`/course/${course.id}/learning`}
              className="btn btn-primary w-100 fw-semibold py-2"
              style={{ fontSize: '0.95rem' }}
            >
              {course.progress === 100 ? (
                <>
                  <i className="bi bi-arrow-clockwise me-2"></i>
                  Review Course
                </>
              ) : course.progress > 0 ? (
                <>
                  <i className="bi bi-play-fill me-2"></i>
                  Continue Learning
                </>
              ) : (
                <>
                  <i className="bi bi-play-circle-fill me-2"></i>
                  Start Learning
                </>
              )}
            </Link>
          </div>
        </div>
      </div>
    </div>
  );

  if (loading) {
    return (
      <div className="container-fluid py-5">
        <div className="row justify-content-center">
          <div className="col-12 text-center">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
            <p className="mt-3 text-muted">Loading your courses...</p>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container-fluid py-5">
        <div className="row justify-content-center">
          <div className="col-12 col-md-6 text-center">
            <div className="alert alert-danger">
              <i className="bi bi-exclamation-triangle-fill me-2"></i>
              {error}
            </div>
            <button 
              className="btn btn-primary" 
              onClick={() => window.location.reload()}
            >
              Retry
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid py-4">
      {/* Payment Success/Error Message */}
      {paymentMessage && (
        <div className="row mb-4">
          <div className="col-12">
            <div className={`alert alert-${paymentMessage.type} alert-dismissible fade show`} role="alert">
              <i className={`bi ${paymentMessage.type === 'success' ? 'bi-check-circle-fill' : 
                paymentMessage.type === 'error' ? 'bi-exclamation-triangle-fill' : 'bi-info-circle-fill'} me-2`}></i>
              {paymentMessage.message}
              <button 
                type="button" 
                className="btn-close" 
                onClick={() => setPaymentMessage(null)}
              ></button>
            </div>
          </div>
        </div>
      )}

      {/* Header Section */}
      <div className="row mb-4">
        <div className="col-12">
          <div className="d-flex flex-column flex-md-row justify-content-between align-items-start align-items-md-center mb-4">
            <div>
              <h1 className="display-5 fw-bold text-secondary mb-2">My Learning Dashboard</h1>
              <p className="text-muted mb-0">
                Track your progress and continue your learning journey
              </p>
            </div>
            <div className="mt-3 mt-md-0">
              <Link to="/courses" className="btn btn-outline-primary">
                <i className="bi bi-search me-2"></i>
                Explore More Courses
              </Link>
            </div>
          </div>
          
          {/* Enhanced Stats Cards */}
          <div className="row my-5 justify-content-around">
            <div className="col-6 col-md-3 mb-3">
              <div className="card bg-primary text-white text-center h-100">
                <div className="card-body py-3">
                  <i className="bi bi-book-fill display-6 mb-2"></i>
                  <h4 className="mb-1">{purchasedCourses.length + freeCourses.length}</h4>
                  <small>Total Courses</small>
                </div>
              </div>
            </div>
            <div className="col-6 col-md-3 mb-3">
              <div className="card bg-success text-white text-center h-100">
                <div className="card-body py-3">
                  <i className="bi bi-check-circle-fill display-6 mb-2"></i>
                  <h4 className="mb-1">
                    {[...purchasedCourses, ...freeCourses].filter(c => c.progress === 100).length}
                  </h4>
                  <small>Completed</small>
                </div>
              </div>
            </div>
            <div className="col-6 col-md-3 mb-3">
              <div className="card bg-warning text-white text-center h-100">
                <div className="card-body py-3">
                  <i className="bi bi-play-circle-fill display-6 mb-2"></i>
                  <h4 className="mb-1">
                    {[...purchasedCourses, ...freeCourses].filter(c => c.progress > 0 && c.progress < 100).length}
                  </h4>
                  <small>In Progress</small>
                </div>
              </div>
            </div>
            <div className="col-6 col-md-3 mb-3">
              <div className="card bg-info text-white text-center h-100">
                <div className="card-body py-3">
                  <i className="bi bi-clock-fill display-6 mb-2"></i>
                  <h4 className="mb-1">
                    {[...purchasedCourses, ...freeCourses].filter(c => c.progress === 0).length}
                  </h4>
                  <small>Not Started</small>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Purchased Courses Section */}
      {purchasedCourses.length > 0 && (
        <div className="row mb-5">
          <div className="col-12">
            <div className="d-flex align-items-center mb-4">
              <h2 className="h3 mb-0 me-3">
                <i className="bi bi-award-fill text-primary me-2"></i>
                Premium Courses
              </h2>
              <span className="badge bg-primary rounded-pill">
                {purchasedCourses.length}
              </span>
            </div>
            <div className="row">
              {purchasedCourses.map((course) => (
                <CourseCard key={course.id} course={course} isPurchased={true} />
              ))}
            </div>
          </div>
        </div>
      )}

      {/* Free Courses Section */}
      {freeCourses.length > 0 && (
        <div className="row mb-5">
          <div className="col-12">
            <div className="d-flex align-items-center mb-4">
              <h2 className="h3 mb-0 me-3">
                <i className="bi bi-gift-fill text-success me-2"></i>
                Free Courses
              </h2>
              <span className="badge bg-success rounded-pill">
                {freeCourses.length}
              </span>
            </div>
            <div className="row">
              {freeCourses.map((course) => (
                <CourseCard key={course.id} course={course} isPurchased={false} />
              ))}
            </div>
          </div>
        </div>
      )}

      {/* Empty State */}
      {purchasedCourses.length === 0 && freeCourses.length === 0 && (
        <div className="row">
          <div className="col-12">
            <div className="text-center py-5">
              <i className="bi bi-mortarboard display-1 text-muted mb-4"></i>
              <h3 className="text-muted mb-3">Start Your Learning Journey</h3>
              <p className="text-muted mb-4">
                You haven't enrolled in any courses yet. Discover amazing courses and start learning today!
              </p>
              <Link to="/courses" className="btn btn-primary btn-lg">
                <i className="bi bi-search me-2"></i>
                Explore Courses
              </Link>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default MyCourses;
