import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Courses = () => {
    const [courses, setCourses] = useState([]);

    useEffect(() => {
        const fetchCourses = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/admin/courses');
                setCourses(response.data);
            } catch (error) {
                console.error('Error fetching courses:', error);
            }
        };

        fetchCourses();
    }, []);

    return (
        <div style={{ padding: '20px' }}>
            <h2 style={{ marginBottom: '20px' }}>All Courses</h2>
            <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px' }}>
                {courses.length === 0 ? (
                    <p>No courses available.</p>
                ) : (
                    courses.map(course => (
                        <div key={course._id} style={{
                            border: '1px solid #ccc',
                            borderRadius: '8px',
                            padding: '15px',
                            width: '300px',
                            boxShadow: '0 2px 8px rgba(0, 0, 0, 0.1)'
                        }}>
                            <img
                                src={course.thumbnailUrl}
                                alt={course.title}
                                style={{ width: '100%', height: '180px', objectFit: 'cover', borderRadius: '5px' }}
                            />
                            <h3 style={{ margin: '10px 0 5px' }}>{course.title}</h3>
                            <p style={{ color: '#555', fontSize: '14px' }}>
                                {course.description?.slice(0, 100)}...
                            </p>
                            <p style={{ margin: '8px 0', fontWeight: 'bold' }}>Instructor: {course.instructor || "N/A"}</p>
                            <p style={{ marginBottom: '10px' }}>Price: â‚¹{course.price || 0}</p>
                            <button style={{
                                backgroundColor: '#007bff',
                                color: '#fff',
                                padding: '8px 12px',
                                border: 'none',
                                borderRadius: '5px',
                                cursor: 'pointer'
                            }}>
                                View Details
                            </button>
                        </div>
                    ))
                )}
            </div>
        </div>
    );
};

export default Courses;
