import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

function MyCourses() {
  const [purchasedCourses, setPurchasedCourses] = useState([]);
  const [freeCourses, setFreeCourses] = useState([]);
  const [loading, setLoading] = useState(true);

  // Mock data - replace with actual API calls
  useEffect(() => {
    // Simulate API call
    setTimeout(() => {
      setPurchasedCourses([
        {
          id: 1,
          title: "Complete React Developer Course",
          instructor: "John Smith",
          price: 89.99,
          thumbnail: "https://images.unsplash.com/photo-1633356122544-f134324a6cee?w=400&h=250&fit=crop",
          enrollmentDate: "2024-01-15",
          progress: 75,
          duration: "40 hours"
        },
        {
          id: 2,
          title: "Advanced JavaScript Concepts",
          instructor: "Sarah Johnson",
          price: 129.99,
          thumbnail: "https://images.unsplash.com/photo-1627398242454-45a1465c2479?w=400&h=250&fit=crop",
          enrollmentDate: "2024-02-10",
          progress: 45,
          duration: "32 hours"
        },
        {
          id: 3,
          title: "Node.js Backend Development",
          instructor: "Mike Wilson",
          price: 99.99,
          thumbnail: "https://images.unsplash.com/photo-1618477247222-acbdb0e159b3?w=400&h=250&fit=crop",
          enrollmentDate: "2024-03-05",
          progress: 20,
          duration: "28 hours"
        },
                {
          id: 4,
          title: "Backend Dev",
          instructor: "Mike Wilson",
          price: 299.99,
          thumbnail: "https://images.unsplash.com/photo-1618477247222-acbdb0e159b3?w=400&h=250&fit=crop",
          enrollmentDate: "2024-03-05",
          progress: 20,
          duration: "28 hours"
        },
                {
          id: 5,
          title: "Backend Development",
          instructor: "Mike Wilson",
          price: 199.99,
          thumbnail: "https://images.unsplash.com/photo-1618477247222-acbdb0e159b3?w=400&h=250&fit=crop",
          enrollmentDate: "2024-03-05",
          progress: 20,
          duration: "28 hours"
        }
      ]);

      setFreeCourses([
        {
          id: 5,
          title: "HTML & CSS Fundamentals",
          instructor: "Emma Davis",
          price: 0,
          thumbnail: "https://images.unsplash.com/photo-1516321318423-f06f85e504b3?w=400&h=250&fit=crop",
          enrollmentDate: "2024-01-20",
          progress: 100,
          duration: "15 hours"
        },
        {
          id: 7,
          title: "Git Version Control Basics",
          instructor: "David Brown",
          price: 0,
          thumbnail: "https://images.unsplash.com/photo-1556075798-4825dfaaf498?w=400&h=250&fit=crop",
          enrollmentDate: "2024-02-28",
          progress: 60,
          duration: "8 hours"
        },
        {
          id: 8,
          title: "Introduction to Web Development",
          instructor: "Lisa Anderson",
          price: 0,
          thumbnail: "https://images.unsplash.com/photo-1498050108023-c5249f4df085?w=400&h=250&fit=crop",
          enrollmentDate: "2024-03-15",
          progress: 30,
          duration: "12 hours"
        },
                {
          id: 9,
          title: "Introduction to Web Dev",
          instructor: "Lisa son",
          price: 1000,
          thumbnail: "https://images.unsplash.com/photo-1498050108023-c5249f4df085?w=400&h=250&fit=crop",
          enrollmentDate: "2024-03-15",
          progress: 20,
          duration: "18 hours"
        }
      ]);

      setLoading(false);
    }, 1000);
  }, []);

  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  const getProgressColor = (progress) => {
    if (progress === 100) return 'bg-success';
    if (progress >= 70) return 'bg-info';
    if (progress >= 40) return 'bg-warning';
    return 'bg-danger';
  };

  const CourseCard = ({ course, isPurchased }) => (
    <div className=" col-12 col-lg-3 col-md-4 col-sm-6 mb-5">
      <div className="card h-100 shadow-sm border-0">
        <div className="position-relative">
          <img
            src={course.thumbnail}
            className="card-img-top"
            alt={course.title}
            style={{ height: '230px', objectFit: 'cover' }}
          />
          <div className="position-absolute top-0 end-0 m-2">
            <span className={`badge ${isPurchased ? 'bg-primary' : 'bg-success'}`}>
              {isPurchased ? 'Purchased' : 'Free'}
            </span>
          </div>
          {course.progress === 100 && (
            <div className="position-absolute top-0 start-0 m-2">
              <span className="badge bg-success">
                <i className="bi bi-check-circle-fill me-1"></i>
                Completed
              </span>
            </div>
          )}
        </div>
        
        <div className="card-body d-flex flex-column">
          <h5 className="card-title text-truncate" title={course.title}>
            {course.title}
          </h5>
          
          <div className="mb-2">
            <small className="text-muted">
              <i className="bi bi-person-fill me-1"></i>
              {course.instructor}
            </small>
          </div>
          
          <div className="mb-2">
            <small className="text-muted">
              <i className="bi bi-clock-fill me-1"></i>
              {course.duration}
            </small>
          </div>
          
          {isPurchased && (
            <div className="mb-2">
              <small className="text-muted">
                <i className="bi bi-currency-rupee me-1"></i>
                {course.price}
              </small>
            </div>
          )}
          
          <div className="mb-3">
            <small className="text-muted">
              <i className="bi bi-calendar-fill me-1"></i>
              Enrolled: {formatDate(course.enrollmentDate)}
            </small>
          </div>
          
          {/* Progress Bar */}
          <div className="mb-3">
            <div className="d-flex justify-content-between align-items-center mb-1">
              <small className="text-muted">Progress</small>
              <small className="text-muted">{course.progress}%</small>
            </div>
            <div className="progress" style={{ height: '6px' }}>
              <div
                className={`progress-bar ${getProgressColor(course.progress)}`}
                role="progressbar"
                style={{ width: `${course.progress}%` }}
                aria-valuenow={course.progress}
                aria-valuemin="0"
                aria-valuemax="100"
              ></div>
            </div>
          </div>
          
          <div className="mt-auto">
            <Link
              to={`/course/${course.id}/learning`}
              className="btn btn-primary w-100"
            >
              {course.progress === 100 ? 'Review Course' : 'Continue Learning'}
              <i className="bi bi-arrow-right ms-2"></i>
            </Link>
          </div>
        </div>
      </div>
    </div>
  );

  if (loading) {
    return (
      <div className="container-fluid py-5">
        <div className="row justify-content-center">
          <div className="col-12 text-center">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
            <p className="mt-3 text-muted">Loading your courses...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid py-4">
      {/* Header Section */}
      <div className="row mb-4">
        <div className="col-12">
          <div className="d-flex flex-column flex-md-row justify-content-between align-items-start align-items-md-center mb-4">
            <div>
              <h1 className="display-5 fw-bold text-secondary mb-2">My Courses</h1>
              <p className="text-muted mb-0">
                Continue your learning journey with your enrolled courses
              </p>
            </div>
            <div className="mt-3 mt-md-0">
              <Link to="/courses" className="btn btn-outline-primary">
                <i className="bi bi-search me-2"></i>
                Browse Courses
              </Link>
            </div>
          </div>
          
          {/* Stats Cards */}
          <div className="row my-5 justify-content-around">
            <div className="col-4 col-md-3">
              <div className="card bg-primary text-white text-center">
                <div className="card-body py-3">
                  <h4 className="mb-1">{purchasedCourses.length + freeCourses.length}</h4>
                  <small>Total Courses</small>
                </div>
              </div>
            </div>
            <div className="col-4 col-md-3">
              <div className="card bg-success text-white text-center">
                <div className="card-body py-3">
                  <h4 className="mb-1">
                    {[...purchasedCourses, ...freeCourses].filter(c => c.progress === 100).length}
                  </h4>
                  <small>Completed</small>
                </div>
              </div>
            </div>
            <div className="col-4 col-md-3">
              <div className="card bg-warning text-white text-center">
                <div className="card-body py-3">
                  <h4 className="mb-1">
                    {[...purchasedCourses, ...freeCourses].filter(c => c.progress > 0 && c.progress < 100).length}
                  </h4>
                  <small>In Progress</small>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Purchased Courses Section */}
      {purchasedCourses.length > 0 && (
        <div className="row mb-5">
          <div className="col-12">
            <div className="d-flex align-items-center mb-4">
              <h2 className="h3 mb-0 me-3">
                <i className="bi bi-cart-check-fill text-primary me-2"></i>
                Purchased Courses
              </h2>
              <span className="badge bg-primary rounded-pill">
                {purchasedCourses.length}
              </span>
            </div>
            <div className="row">
              {purchasedCourses.map((course) => (
                <CourseCard key={course.id} course={course} isPurchased={true} />
              ))}
            </div>
          </div>
        </div>
      )}

      {/* Free Courses Section */}
      {freeCourses.length > 0 && (
        <div className="row mb-5">
          <div className="col-12">
            <div className="d-flex align-items-center mb-4">
              <h2 className="h3 mb-0 me-3">
                <i className="bi bi-gift-fill text-success me-2"></i>
                Free Courses
              </h2>
              <span className="badge bg-success rounded-pill">
                {freeCourses.length}
              </span>
            </div>
            <div className="row">
              {freeCourses.map((course) => (
                <CourseCard key={course.id} course={course} isPurchased={false} />
              ))}
            </div>
          </div>
        </div>
      )}

      {/* Empty State */}
      {purchasedCourses.length === 0 && freeCourses.length === 0 && (
        <div className="row">
          <div className="col-12">
            <div className="text-center py-5">
              <i className="bi bi-book display-1 text-muted mb-4"></i>
              <h3 className="text-muted mb-3">No Courses Yet</h3>
              <p className="text-muted mb-4">
                You haven't enrolled in any courses yet. Start your learning journey today!
              </p>
              <Link to="/courses" className="btn btn-primary btn-lg">
                <i className="bi bi-search me-2"></i>
                Browse Courses
              </Link>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default MyCourses;
