// src/pages/Home.jsx
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

export default function Home() {
    const [courses, setCourses] = useState([]);

    useEffect(() => {
        const fetchFeaturedCourses = async () => {
            try {
                const res = await axios.get('http://localhost:5000/api/admin/courses'); // ✅ Full URL
                setCourses(res.data.slice(0, 3)); // Show only first 3
            } catch (err) {
                console.error('Error fetching courses:', err);
            }
        };

        fetchFeaturedCourses();
    }, []);

    return (
        <div style={{ padding: '2rem' }}>
            <section style={{ textAlign: 'center', marginBottom: '3rem' }}>
                <h1>Welcome to LearnHub</h1>
                <p>Your journey to learning starts here!</p>
            </section>

            <section>
                <h2>Featured Courses</h2>
                <div style={{
                    display: 'flex',
                    flexWrap: 'wrap',
                    gap: '1.5rem',
                    marginTop: '1rem'
                }}>
                    {courses.map(course => (
                        <div key={course._id} style={{
                            border: '1px solid #ddd',
                            borderRadius: '10px',
                            padding: '1rem',
                            width: '300px'
                        }}>
                            <img src={course.thumbnailUrl} alt={course.title} style={{ width: '100%', height: '150px', objectFit: 'cover', borderRadius: '8px' }} />
                            <h3>{course.title}</h3>
                            <p>{course.description?.slice(0, 100)}...</p>
                            <p><strong>Price:</strong> ₹{course.price}</p>
                        </div>
                    ))}
                </div>
                <div style={{ marginTop: '2rem', textAlign: 'center' }}>
                    <Link to="/courses">
                        <button style={{
                            padding: '10px 20px',
                            fontSize: '16px',
                            borderRadius: '6px',
                            backgroundColor: '#007bff',
                            color: '#fff',
                            border: 'none',
                            cursor: 'pointer'
                        }}>View All Courses</button>
                    </Link>
                </div>
            </section>
        </div>
    );
}
