import React, { useState, useEffect } from "react";
import axios from "axios";

// CourseCard Component
const CourseCard = ({ course }) => {
  const formatPrice = (price) => {
    return price === 0 ? "Free" : `â‚¹${price.toLocaleString()}`;
  };

  const getLevelBadgeColor = (level) => {
    switch (level?.toLowerCase()) {
      case "beginner": return "bg-success";
      case "intermediate": return "bg-warning";
      case "advanced": return "bg-danger";
      case "professional": return "bg-dark";
      default: return "bg-secondary";
    }
  };

  const getPublishStatus = (isPublished) => {
    return isPublished ? "Published" : "Draft";
  };

  const getPublishStatusColor = (isPublished) => {
    return isPublished ? "bg-success" : "bg-warning text-dark";
  };

  return (
    <div className="card shadow-sm h-100 course-card">
      <div className="position-relative">
        {course.thumbnailUrl ? (
          <img
            src={`http://localhost:5000${course.thumbnailUrl}`}
            className="card-img-top"
            alt={course.title}
            style={{ height: "200px", objectFit: "cover" }}
            onError={(e) => {
              e.target.src = "https://via.placeholder.com/300x200?text=No+Image";
            }}
          />
        ) : (
          <div
            className="card-img-top bg-light d-flex align-items-center justify-content-center"
            style={{ height: "200px" }}
          >
            <i className="fas fa-image fa-3x text-muted"></i>
          </div>
        )}

        {/* Status Badge */}
        <span className={`badge position-absolute top-0 start-0 m-2 ${getPublishStatusColor(course.isPublished)}`}>
          {getPublishStatus(course.isPublished)}
        </span>

        {/* Price Badge */}
        <span className="badge bg-primary position-absolute top-0 end-0 m-2">
          {formatPrice(course.price)}
        </span>
      </div>

      <div className="card-body d-flex flex-column">
        <h6 className="card-title fw-bold mb-2" title={course.title}>
          {course.title.length > 50 ? `${course.title.substring(0, 50)}...` : course.title}
        </h6>

        <p className="card-text text-muted small mb-2" title={course.description}>
          {course.description.length > 80 ? `${course.description.substring(0, 80)}...` : course.description}
        </p>

        <div className="mb-2">
          <small className="text-muted">
            <i className="fas fa-user me-1"></i>
            {course.instructor}
          </small>
        </div>

        <div className="mb-2">
          <small className="text-muted">
            <i className="fas fa-clock me-1"></i>
            {course.duration}
          </small>
        </div>

        <div className="mb-2">
          <small className="text-muted">
            <i className="fas fa-globe me-1"></i>
            {course.language}
          </small>
        </div>

        <div className="mb-3">
          <span className={`badge ${getLevelBadgeColor(course.level)} me-2`}>
            {course.level}
          </span>
          {course.category && (
            <span className="badge bg-outline-secondary small">
              {course.category}
            </span>
          )}
        </div>

        {/* Tags */}
        {course.tags && course.tags.length > 0 && (
          <div className="mb-2">
            {course.tags.slice(0, 2).map((tag, index) => (
              <span key={index} className="badge bg-light text-dark me-1 small">
                #{tag}
              </span>
            ))}
            {course.tags.length > 2 && (
              <span className="badge bg-light text-muted small">
                +{course.tags.length - 2} more
              </span>
            )}
          </div>
        )}

        <div className="mt-auto">
          <div className="d-flex gap-2">
            <button className="btn btn-primary btn-sm flex-fill">
              <i className="fas fa-eye me-1"></i>
              View
            </button>
            <button className="btn btn-outline-secondary btn-sm flex-fill">
              <i className="fas fa-edit me-1"></i>
              Edit
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

function Courses() {
  const [courses, setCourses] = useState([]);
  const [categories, setCategories] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  // Filter and UI states
  const [selectedCategory, setSelectedCategory] = useState("All");
  const [selectedSubcategory, setSelectedSubcategory] = useState("All");
  const [filter, setFilter] = useState("All"); // All, Free, Paid
  const [sortOrder, setSortOrder] = useState("default"); // default, asc, desc
  const [searchQuery, setSearchQuery] = useState("");
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 992);
  const [expandedCategories, setExpandedCategories] = useState({});
  const [expandedSubcategories, setExpandedSubcategories] = useState({});

  // Fetch courses from backend
  useEffect(() => {
    const fetchCourses = async () => {
      try {
        setIsLoading(true);
        const response = await axios.get("http://localhost:5000/api/admin/courses", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("adminToken")}`,
          },
        });
        console.log("Fetched courses:", response.data);
        setCourses(response.data);
      } catch (err) {
        console.error("Error fetching courses:", err);
        setError("Failed to fetch courses");
      } finally {
        setIsLoading(false);
      }
    };

    fetchCourses();
  }, []);

  // Fetch categories from backend
  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/admin/categories", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("adminToken")}`,
          },
        });
        console.log("Fetched categories:", response.data);
        setCategories(response.data);
      } catch (err) {
        console.error("Error fetching categories:", err);
      }
    };

    fetchCategories();
  }, []);

  // Handle window resize
  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth < 992);
    };

    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // Create categories with subcategories structure
  const categoriesWithSub = React.useMemo(() => {
    if (!categories.length) return [];

    // Get main categories (level 1)
    const mainCategories = categories.filter(cat => cat.level === 1);

    return mainCategories.map(mainCat => {
      // Get subcategories for this main category
      const subcategories = categories.filter(cat =>
        cat.level === 2 &&
        (cat.parentCategory === mainCat._id || cat.parentCategory?._id === mainCat._id)
      );

      // Get sub-subcategories for each subcategory
      const subcategoriesWithSubs = subcategories.map(subCat => {
        const subSubcategories = categories.filter(cat =>
          cat.level === 3 &&
          (cat.parentCategory === subCat._id || cat.parentCategory?._id === subCat._id)
        );

        if (subSubcategories.length > 0) {
          return {
            name: subCat.name,
            items: subSubcategories.map(subSubCat => subSubCat.name)
          };
        }
        return subCat.name;
      });

      return {
        name: mainCat.name,
        subcategories: subcategoriesWithSubs
      };
    });
  }, [categories]);

  // Filter courses based on selected filters
  const filteredCourses = React.useMemo(() => {
    let filtered = [...courses];

    // Filter by category
    if (selectedCategory !== "All") {
      filtered = filtered.filter(course => {
        // Get category name by ID
        const categoryObj = categories.find(cat => cat._id === course.category);
        const categoryName = categoryObj?.name || course.category;

        if (selectedSubcategory === "All") {
          // Show all courses in this main category and its subcategories
          return categoryName === selectedCategory ||
            categories.some(cat =>
              cat.name === selectedSubcategory &&
              categories.some(parent =>
                parent.name === selectedCategory &&
                (cat.parentCategory === parent._id || cat.parentCategory?._id === parent._id)
              )
            );
        } else {
          // Show courses in specific subcategory
          const subcategoryObj = categories.find(cat => cat.name === selectedSubcategory);
          return course.category === subcategoryObj?._id ||
            course.subcategory === subcategoryObj?._id ||
            course.sub_subcategory === subcategoryObj?._id;
        }
      });
    }

    // Filter by price (Free/Paid)
    if (filter !== "All") {
      filtered = filtered.filter(course =>
        filter === "Free" ? course.price === 0 : course.price > 0
      );
    }

    // Filter by search query
    if (searchQuery.trim()) {
      const query = searchQuery.toLowerCase();
      filtered = filtered.filter(course =>
        course.title.toLowerCase().includes(query) ||
        course.description.toLowerCase().includes(query) ||
        course.instructor.toLowerCase().includes(query) ||
        course.tags?.some(tag => tag.toLowerCase().includes(query))
      );
    }

    // Sort courses
    if (sortOrder === "asc") {
      filtered.sort((a, b) => a.title.localeCompare(b.title));
    } else if (sortOrder === "desc") {
      filtered.sort((a, b) => b.title.localeCompare(a.title));
    } else {
      // Default sort by creation date (newest first)
      filtered.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
    }

    return filtered;
  }, [courses, categories, selectedCategory, selectedSubcategory, filter, sortOrder, searchQuery]);

  // Helper functions
  const getCategoryIcon = (category, subcategory = null, item = null) => {
    // You can customize icons based on category names
    const iconMap = {
      "Technology": "fas fa-laptop-code text-primary",
      "Business": "fas fa-briefcase text-success",
      "Design": "fas fa-palette text-warning",
      "Marketing": "fas fa-bullhorn text-info",
      "Health": "fas fa-heartbeat text-danger",
      "Music": "fas fa-music text-purple",
      "Photography": "fas fa-camera text-dark",
      "Language": "fas fa-language text-secondary",
    };

    return <i className={iconMap[category] || "fas fa-folder text-muted"}></i>;
  };

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  const toggleCategory = (categoryName) => {
    setExpandedCategories(prev => ({
      ...prev,
      [categoryName]: !prev[categoryName]
    }));
  };

  const toggleSubcategory = (subcategoryKey) => {
    setExpandedSubcategories(prev => ({
      ...prev,
      [subcategoryKey]: !prev[subcategoryKey]
    }));
  };

  const handleCategorySelect = (category, subcategory = "All") => {
    setSelectedCategory(category);
    setSelectedSubcategory(subcategory);
    if (isMobile) {
      setSidebarOpen(false);
    }
  };

  const handleClearSearch = () => {
    setSearchQuery("");
  };

  // Loading state
  if (isLoading) {
    return (
      <div className="container-fluid d-flex justify-content-center align-items-center" style={{ minHeight: "60vh" }}>
        <div className="text-center">
          <div className="spinner-border text-primary mb-3" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <h5 className="text-muted">Loading courses...</h5>
        </div>
      </div>
    );
  }

  // Error state
  if (error) {
    return (
      <div className="container-fluid d-flex justify-content-center align-items-center" style={{ minHeight: "60vh" }}>
        <div className="text-center">
          <div className="mb-4">
            <i className="fas fa-exclamation-triangle fa-4x text-warning"></i>
          </div>
          <h4 className="text-muted mb-3">Error Loading Courses</h4>
          <p className="text-muted mb-4">{error}</p>
          <button
            className="btn btn-primary"
            onClick={() => window.location.reload()}
          >
            <i className="fas fa-refresh me-2"></i>
            Retry
          </button>
        </div>
      </div>
    );
  }
  return (
    <>
      {/* Add Font Awesome CSS and Custom Styles */}
      <link
        rel="stylesheet"
        href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0/css/all.min.css"
      />
      <style>{`
        .text-orange { color: #ff6b35; }
        .text-cyan { color: #17a2b8; }
        .text-purple { color: #6f42c1; }
        
        /* Sidebar Styles */
        .sidebar-overlay {
          position: fixed;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          background-color: rgba(0, 0, 0, 0.5);
          z-index: 1040;
        }
        
        .sidebar-mobile {
          position: fixed;
          top: 0;
          left: 0;
          height: 100vh;
          width: 300px;
          z-index: 1050;
          transform: translateX(-100%);
          transition: transform 0.3s ease-in-out;
          overflow-y: auto;
        }
        
        .sidebar-mobile.show {
          transform: translateX(0);
        }
        
        .sidebar-desktop {
          position: sticky;
          top: 10px;
          height: calc(100vh - 40px);
          overflow-y: auto;
        }
        
        /* Smooth transitions */
        .list-group-item {
          transition: all 0.3s ease;
        }
        
        .list-group-item:hover {
          background-color: #f8f9fa;
        }
        
        /* Course card improvements */
        .course-card {
          transition: transform 0.2s ease, box-shadow 0.2s ease;
          border: 1px solid #e9ecef;
        }
        
        .course-card:hover {
          transform: translateY(-5px);
          box-shadow: 0 8px 25px rgba(0,0,0,0.15) !important;
        }
        
        /* Mobile responsive adjustments */
        @media (max-width: 576px) {
          .sidebar-mobile {
            width: 280px;
          }
          
          .mobile-cards .col {
            flex: 0 0 auto;
            width: 50% !important;
            padding: 0.25rem !important;
          }
          
          .mobile-cards .card {
            font-size: 0.75rem;
            height: 100%;
          }
          
          .mobile-cards .card-img-top {
            height: 100px !important;
            object-fit: cover;
          }
          
          .mobile-cards .card-title {
            font-size: 0.7rem !important;
            line-height: 1.2;
            height: 2rem;
            overflow: hidden;
            display: -webkit-box;
            -webkit-line-clamp: 2;
            -webkit-box-orient: vertical;
          }
          
          .mobile-cards .card-text {
            font-size: 0.6rem !important;
            line-height: 1.1;
            height: 1.5rem;
            overflow: hidden;
            display: -webkit-box;
            -webkit-line-clamp: 2;
            -webkit-box-orient: vertical;
          }
          
          .mobile-cards .btn {
            font-size: 0.6rem !important;
            padding: 0.25rem 0.5rem !important;
          }
          
          .mobile-cards .badge {
            font-size: 0.6rem !important;
          }
          
          .mobile-cards small {
            font-size: 0.5rem !important;
          }
        }
        
        @media (max-width: 400px) {
          #courses-sidebar {
            padding: 0.7rem !important;
          }
          .list-group-item {
            font-size: 0.8rem;
            padding: 0.5rem;
          }
        }
      `}</style>

      <div className="container-fluid">
        {/* Mobile Overlay */}
        {isMobile && sidebarOpen && (
          <div
            className="sidebar-overlay"
            onClick={() => setSidebarOpen(false)}
          ></div>
        )}

        <div className="row">
          {/* Enhanced Sidebar */}
          <div className={`col-lg-3 ${isMobile ? "" : "col-md-4"}`}>
            <div
              id="courses-sidebar"
              className={`bg-white shadow rounded ${isMobile
                  ? `sidebar-mobile ${sidebarOpen ? "show" : ""}`
                  : `sidebar-desktop ${sidebarOpen ? "" : "d-none"}`
                }`}
              style={{
                minHeight: isMobile ? "100vh" : "80vh",
                ...(isMobile ? {} : { top: "20px" }),
              }}
            >
              <div className="p-3">
                {/* Sidebar Header */}
                <div className="d-flex justify-content-between align-items-center mb-3">
                  <h5 className="mb-0 fw-bold">
                    <i className="fas fa-filter me-2 text-primary"></i>
                    Categories
                  </h5>
                  {isMobile && (
                    <button
                      className="btn btn-sm btn-light"
                      onClick={() => setSidebarOpen(false)}
                      aria-label="Close sidebar"
                    >
                      <i className="fas fa-times"></i>
                    </button>
                  )}
                </div>

                {/* All Categories Option */}
                <div className="mb-3">
                  <div
                    className={`list-group-item list-group-item-action p-3 border-0 rounded ${selectedCategory === "All"
                        ? "active text-white"
                        : "text-dark"
                      }`}
                    style={{ cursor: "pointer", fontSize: "0.9rem" }}
                    onClick={() => handleCategorySelect("All")}
                  >
                    <span className="d-flex align-items-center">
                      <i className="fas fa-th-large text-primary"></i>
                      <span className="ms-2 fw-medium">All Categories</span>
                    </span>
                  </div>
                </div>

                {/* Categories List */}
                <div className="list-group list-group-flush">
                  {categoriesWithSub.map((category) => (
                    <div key={category.name} className="mb-1">
                      {/* Main Category */}
                      <div
                        className={`list-group-item list-group-item-action d-flex justify-content-between align-items-center p-3 border-0 rounded ${selectedCategory === category.name &&
                            selectedSubcategory === "All"
                            ? "active text-white"
                            : "text-dark"
                          }`}
                        style={{ cursor: "pointer", fontSize: "0.9rem" }}
                        onClick={() => {
                          handleCategorySelect(category.name);
                          if (category.subcategories.length > 0) {
                            toggleCategory(category.name);
                          }
                        }}
                      >
                        <span className="d-flex align-items-center">
                          {getCategoryIcon(category.name)}
                          <span className="ms-2 fw-medium">
                            {category.name}
                          </span>
                        </span>
                        {category.subcategories.length > 0 && (
                          <span
                            className={`badge ${selectedCategory === category.name &&
                                selectedSubcategory === "All"
                                ? "bg-light text-dark"
                                : "bg-secondary"
                              }`}
                          >
                            <i
                              className={`fas ${expandedCategories[category.name]
                                  ? "fa-minus"
                                  : "fa-plus"
                                }`}
                            ></i>
                          </span>
                        )}
                      </div>

                      {/* Subcategories */}
                      {category.subcategories.length > 0 &&
                        expandedCategories[category.name] && (
                          <div className="ms-3 mt-1">
                            {category.subcategories.map((subcategory) => {
                              // Handle nested subcategories (like Frontend/Backend)
                              if (
                                typeof subcategory === "object" &&
                                subcategory.items
                              ) {
                                const subcategoryKey = `${category.name}-${subcategory.name}`;
                                return (
                                  <div key={subcategory.name} className="mb-2">
                                    {/* Nested Category Header */}
                                    <div
                                      className={`list-group-item list-group-item-action d-flex justify-content-between align-items-center p-2 border-0 rounded ${selectedCategory === category.name &&
                                          selectedSubcategory === subcategory.name
                                          ? "active text-white"
                                          : "bg-light text-dark"
                                        }`}
                                      style={{
                                        cursor: "pointer",
                                        fontSize: "0.85rem",
                                      }}
                                      onClick={() => {
                                        handleCategorySelect(
                                          category.name,
                                          subcategory.name
                                        );
                                        toggleSubcategory(subcategoryKey);
                                      }}
                                    >
                                      <span className="d-flex align-items-center">
                                        {getCategoryIcon(
                                          category.name,
                                          subcategory.name
                                        )}
                                        <span className="ms-2">
                                          {subcategory.name}
                                        </span>
                                      </span>
                                      <span
                                        className={`badge ${selectedCategory === category.name &&
                                            selectedSubcategory ===
                                            subcategory.name
                                            ? "bg-light text-dark"
                                            : "bg-secondary"
                                          }`}
                                      >
                                        <i
                                          className={`fas ${expandedSubcategories[
                                              subcategoryKey
                                            ]
                                              ? "fa-minus"
                                              : "fa-plus"
                                            }`}
                                        ></i>
                                      </span>
                                    </div>

                                    {/* Nested Items */}
                                    {expandedSubcategories[subcategoryKey] && (
                                      <div className="ms-3 mt-1">
                                        {subcategory.items.map((item) => (
                                          <div
                                            key={item}
                                            className={`list-group-item list-group-item-action p-2 border-0 rounded mb-1 ${selectedCategory ===
                                                category.name &&
                                                selectedSubcategory === item
                                                ? "active text-white"
                                                : "text-dark"
                                              }`}
                                            style={{
                                              cursor: "pointer",
                                              fontSize: "0.8rem",
                                              borderLeft: "3px solid #28a745",
                                            }}
                                            onClick={() =>
                                              handleCategorySelect(
                                                category.name,
                                                item
                                              )
                                            }
                                          >
                                            <span className="d-flex align-items-center">
                                              {getCategoryIcon(
                                                category.name,
                                                subcategory.name,
                                                item
                                              )}
                                              <span className="ms-2">
                                                {item}
                                              </span>
                                            </span>
                                          </div>
                                        ))}
                                      </div>
                                    )}
                                  </div>
                                );
                              } else {
                                // Regular subcategory
                                return (
                                  <div
                                    key={subcategory}
                                    className={`list-group-item list-group-item-action p-2 border-0 rounded mb-1 ${selectedCategory === category.name &&
                                        selectedSubcategory === subcategory
                                        ? "active text-white"
                                        : "text-dark"
                                      }`}
                                    style={{
                                      cursor: "pointer",
                                      fontSize: "0.85rem",
                                      borderLeft: "3px solid #007bff",
                                    }}
                                    onClick={() =>
                                      handleCategorySelect(
                                        category.name,
                                        subcategory
                                      )
                                    }
                                  >
                                    <span className="d-flex align-items-center">
                                      {getCategoryIcon(
                                        category.name,
                                        null,
                                        subcategory
                                      )}
                                      <span className="ms-2">
                                        {subcategory}
                                      </span>
                                    </span>
                                  </div>
                                );
                              }
                            })}
                          </div>
                        )}
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>

          {/* Main Content */}
          <div className={`${sidebarOpen ? "col-lg-9 col-md-8" : "col-12"}`}>
            {/* Header Section */}
            <div className="d-flex justify-content-between align-items-center p-3 flex-wrap">
              <div className="d-flex align-items-center mb-2 mb-md-0">
                <button
                  id="sidebar-toggle-btn"
                  className="btn btn-outline-primary me-3"
                  onClick={toggleSidebar}
                >
                  <i
                    className={`fas ${sidebarOpen ? "fa-chevron-left" : "fa-bars"
                      }`}
                  ></i>
                </button>
                <h2 className="fw-bold mb-0">
                  <i className="fas fa-graduation-cap me-2 text-primary"></i>
                  Courses
                </h2>
              </div>

              {/* Controls */}
              <div className="d-flex gap-2 flex-wrap">
                {/* Search Input */}
                <div className="position-relative">
                  <input
                    type="text"
                    className="form-control form-control-sm"
                    placeholder="Search courses..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    style={{ minWidth: "200px" }}
                  />
                  {searchQuery && (
                    <button
                      className="btn btn-sm btn-link position-absolute top-50 end-0 translate-middle-y text-muted"
                      onClick={handleClearSearch}
                      style={{ border: "none", background: "none" }}
                    >
                      <i className="fas fa-times"></i>
                    </button>
                  )}
                </div>

                <select
                  className="form-select form-select-sm"
                  value={sortOrder}
                  onChange={(e) => setSortOrder(e.target.value)}
                  style={{ minWidth: "120px" }}
                >
                  <option value="default">Sort By</option>
                  <option value="asc">A-Z</option>
                  <option value="desc">Z-A</option>
                </select>

                <select
                  className="form-select form-select-sm"
                  value={filter}
                  onChange={(e) => setFilter(e.target.value)}
                  style={{ minWidth: "100px" }}
                >
                  <option value="All">Filter</option>
                  <option value="Free">Free</option>
                  <option value="Paid">Paid</option>
                </select>
              </div>
            </div>

            {/* Breadcrumb */}
            {selectedCategory !== "All" && (
              <nav aria-label="breadcrumb" className="px-3">
                <ol className="breadcrumb mb-0 bg-light rounded px-3 py-2 mb-3">
                  <li className="breadcrumb-item">
                    <button
                      className="btn btn-link p-0 text-decoration-none small"
                      onClick={() => handleCategorySelect("All")}
                    >
                      <i className="fas fa-home me-1"></i>All
                    </button>
                  </li>
                  <li className="breadcrumb-item">
                    <button
                      className="btn btn-link p-0 text-decoration-none small"
                      onClick={() => handleCategorySelect(selectedCategory)}
                    >
                      {selectedCategory}
                    </button>
                  </li>
                  {selectedSubcategory !== "All" && (
                    <li
                      className="breadcrumb-item active small"
                      aria-current="page"
                    >
                      {selectedSubcategory}
                    </li>
                  )}
                </ol>
              </nav>
            )}

            {/* Course Results Info */}
            <div className="px-3 mb-3">
              <div className="d-flex justify-content-between align-items-center flex-wrap">
                <span className="text-muted fw-medium">
                  <i className="fas fa-search me-1"></i>
                  {filteredCourses.length} course{filteredCourses.length !== 1 ? "s" : ""} found
                </span>

                <div className="d-flex gap-2 flex-wrap">
                  {selectedCategory !== "All" && (
                    <span className="badge bg-primary">
                      <i className="fas fa-tag me-1"></i>
                      {selectedSubcategory !== "All" ? selectedSubcategory : selectedCategory}
                    </span>
                  )}
                  {filter !== "All" && (
                    <span className="badge bg-success">
                      <i className="fas fa-filter me-1"></i>
                      {filter}
                    </span>
                  )}
                  {searchQuery && (
                    <span className="badge bg-info">
                      <i className="fas fa-search me-1"></i>
                      "{searchQuery}"
                    </span>
                  )}
                </div>
              </div>
            </div>

            {/* Courses Grid */}
            <div className={`row px-3 ${isMobile ? 'mobile-cards' : ''}`}>
              {filteredCourses.length > 0 ? (
                filteredCourses.map((course) => (
                  <div
                    key={course.id || course._id}
                    className={`${isMobile ? 'col-6' : 'col-12 col-sm-6 col-md-4 col-lg-3'} mb-4`}
                  >
                    <CourseCard course={course} />
                  </div>
                ))
              ) : (
                <div className="col-12 text-center py-5">
                  <div className="mb-4">
                    <i className="fas fa-search fa-4x text-muted opacity-50"></i>
                  </div>
                  <h4 className="text-muted mb-3">No courses found</h4>
                  <p className="text-muted mb-4">
                    We couldn't find any courses matching your current filters.
                    <br />
                    Try adjusting your search criteria or browse other categories.
                  </p>
                  <button
                    className="btn btn-primary"
                    onClick={() => {
                      setSelectedCategory("All");
                      setSelectedSubcategory("All");
                      setFilter("All");
                      setSortOrder("default");
                      setSearchQuery("");
                    }}
                  >
                    <i className="fas fa-refresh me-2"></i>
                    Reset All Filters
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Courses;
