import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

function CourseDetail() {
    const { id } = useParams();
    const navigate = useNavigate();
    const [course, setCourse] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchCourse = async () => {
            try {
                const res = await axios.get(`http://localhost:5000/api/admin/course/${id}`);
                setCourse(res.data);
            } catch (err) {
                console.error("Error fetching course:", err);
            } finally {
                setLoading(false);
            }
        };

        fetchCourse();
    }, [id]);

    const handleAddToCart = async () => {
        try {
            const token = localStorage.getItem("token");
            if (!token) {
                navigate("/login");
                return;
            }

            const response = await axios.post(
                `${process.env.REACT_APP_API_URL}/api/cart`,
                { courseId: course._id },
                {
                    headers: {
                        Authorization: `Bearer ${token}`,
                        "Content-Type": "application/json"
                    }
                    // Remove withCredentials unless using cookies
                }
            );

            if (response.data.success) {
                alert("Course added to cart!");
            }
        } catch (err) {
            console.error("Cart error:", err.response?.data || err.message);

            if (err.response?.status === 401) {
                localStorage.removeItem("token");
                navigate("/login");
            } else {
                alert(err.response?.data?.message || "Failed to add to cart");
            }
        }
    };
    const handleBuyNow = () => {
        const token = localStorage.getItem("token");
        if (!token) return navigate("/login");

        navigate(`/checkout/${course._id}`);
    };

    if (loading) return <h3 className="text-center mt-5">Loading course...</h3>;
    if (!course) return <h3 className="text-center mt-5 text-danger">Course not found!</h3>;

    return (
        <div className="container mt-5">
            <h2 className="mb-3">{course.title}</h2>
         <img
  src={
    course.thumbnailUrl?.startsWith("http")
      ? course.thumbnailUrl
      : `http://localhost:5000${course.thumbnailUrl}`
  }
  className="card-img-top"
  alt={course.title}
  style={{ height: "180px", objectFit: "cover" }}
/>
            <p><strong>Instructor:</strong> {course.instructor || "N/A"}</p>
            <p><strong>Category:</strong> {course.category || "N/A"}</p>
            <p><strong>Duration:</strong> {course.duration || "N/A"}</p>
            <p className="mt-3">{course.description}</p>
            <p><strong>Price:</strong> {course.price === 0 ? "Free" : `₹${course.price}`}</p>

            {course.price > 0 && (
                <div className="mt-3 d-flex gap-2">
                    <button className="btn btn-primary" onClick={handleAddToCart}>
                        Add to Cart
                    </button>
                    <button className="btn btn-success" onClick={handleBuyNow}>
                        Buy Now
                    </button>
                </div>
            )}
        </div>
    );
}

export default CourseDetail;
