/* eslint-disable no-unused-vars */
/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useState, useEffect } from 'react';
import { AlertCircle, CreditCard, Shield, Truck, Clock } from 'lucide-react';
import { loadStripe } from '@stripe/stripe-js';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

// Initialize Stripe (replace with your publishable key)
const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY);

const Checkout = () => {
    const navigate = useNavigate();

    const [formData, setFormData] = useState({
        email: '',
        firstName: '',
        lastName: '',
        phone: '',
        address: '',
        city: '',
        state: '',
        pincode: '',
        paymentMethod: 'card'
    });

    const [cartItems, setCartItems] = useState([]);
    const [loading, setLoading] = useState(false);
    const [pageLoading, setPageLoading] = useState(true);
    const [errors, setErrors] = useState({});

    // Get user info
    const token = localStorage.getItem('token');
    const user = JSON.parse(localStorage.getItem('user') || '{}');

    // Indian states for dropdown
    const indianStates = [
        'Andhra Pradesh', 'Arunachal Pradesh', 'Assam', 'Bihar', 'Chhattisgarh',
        'Goa', 'Gujarat', 'Haryana', 'Himachal Pradesh', 'Jharkhand', 'Karnataka',
        'Kerala', 'Madhya Pradesh', 'Maharashtra', 'Manipur', 'Meghalaya', 'Mizoram',
        'Nagaland', 'Odisha', 'Punjab', 'Rajasthan', 'Sikkim', 'Tamil Nadu',
        'Telangana', 'Tripura', 'Uttar Pradesh', 'Uttarakhand', 'West Bengal',
        'Delhi', 'Jammu and Kashmir', 'Ladakh', 'Puducherry'
    ];

    // Fetch cart items on component mount
    useEffect(() => {
        const fetchCartData = async () => {
            try {
                const token = localStorage.getItem("token");

                if (!token) {
                    navigate("/login");
                    return;
                }

                console.log("Fetching cart data for checkout...");
                const response = await axios.get(
                    `${process.env.REACT_APP_API_URL}/api/cart`,
                    {
                        headers: {
                            Authorization: `Bearer ${token}`,
                        }
                    }
                );

                console.log("Cart data received for checkout:", response.data);

                // Process cart items - handle both response formats
                if (response.data && response.data.items) {
                    const processedItems = response.data.items.map(item => ({
                        _id: item.course._id,
                        courseId: item.course._id, // âœ… FIXED: Use _id instead of full object
                        title: item.course.title,
                        instructor: item.course.instructor || "N/A",
                        price: item.course.discountedPrice || item.course.price || 0, // âœ… ADDED: Check discounted price first
                        image: item.course.thumbnailUrl ?
                            `${process.env.REACT_APP_API_URL || 'http://localhost:5000'}${item.course.thumbnailUrl}` :
                            "https://images.unsplash.com/photo-1633356122544-f134324a6cee?w=150&h=100&fit=crop",
                        duration: item.course.duration || "N/A",
                        quantity: item.quantity || 1,
                    }));

                    setCartItems(processedItems);
                } else if (response.data && Array.isArray(response.data)) {
                    // If API returns array directly
                    const processedItems = response.data.map(item => ({
                        _id: item.course._id,
                        courseId: item.course._id, // âœ… FIXED: Use _id instead of full object
                        title: item.course.title,
                        instructor: item.course.instructor || "N/A",
                        price: item.course.discountedPrice || item.course.price || 0, // âœ… ADDED: Check discounted price first
                        image: item.course.thumbnailUrl ?
                            `${process.env.REACT_APP_API_URL || 'http://localhost:5000'}${item.course.thumbnailUrl}` :
                            "https://images.unsplash.com/photo-1633356122544-f134324a6cee?w=150&h=100&fit=crop",
                        duration: item.course.duration || "N/A",
                        quantity: item.quantity || 1,
                    }));

                    setCartItems(processedItems);
                }

                // Pre-fill email if user is logged in
                if (user.email) {
                    setFormData(prev => ({ ...prev, email: user.email }));
                }

            } catch (error) {
                console.error("Error fetching cart data for checkout:", error);

                if (error.response?.status === 401) {
                    console.log("Unauthorized - redirecting to login");
                    localStorage.removeItem("token");
                    navigate("/login");
                } else {
                    console.error("Checkout cart fetch error:", error.response?.data?.message || error.message);
                    // Show user-friendly error message
                    alert("Failed to load cart data. Please try again.");
                }
            } finally {
                setPageLoading(false);
            }
        };

        fetchCartData();
    }, [navigate, user.email]);

    // Calculate totals
    const subtotal = cartItems.reduce((sum, item) => {
        const price = item.price || 0;
        const quantity = item.quantity || 1;
        return sum + (price * quantity);
    }, 0);

    const shipping = subtotal > 50000 ? 0 : 500; // Free shipping above â‚¹50,000
    const tax = Math.round(subtotal * 0.18); // 18% GST
    const total = subtotal + shipping + tax;

    // Format currency in Indian format
    const formatCurrency = (amount) => {
        return new Intl.NumberFormat('en-IN', {
            style: 'currency',
            currency: 'INR',
            minimumFractionDigits: 0
        }).format(amount);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));

        // Clear error when user starts typing
        if (errors[name]) {
            setErrors(prev => ({ ...prev, [name]: '' }));
        }
    };

    const validateForm = () => {
        const newErrors = {};

        if (!formData.email) newErrors.email = 'Email is required';
        else if (!/\S+@\S+\.\S+/.test(formData.email)) newErrors.email = 'Email is invalid';

        if (!formData.firstName) newErrors.firstName = 'First name is required';
        if (!formData.lastName) newErrors.lastName = 'Last name is required';
        if (!formData.phone) newErrors.phone = 'Phone number is required';
        else if (!/^[6-9]\d{9}$/.test(formData.phone)) newErrors.phone = 'Enter valid 10-digit Indian mobile number';

        if (!formData.address) newErrors.address = 'Address is required';
        if (!formData.city) newErrors.city = 'City is required';
        if (!formData.state) newErrors.state = 'State is required';
        if (!formData.pincode) newErrors.pincode = 'PIN code is required';
        else if (!/^\d{6}$/.test(formData.pincode)) newErrors.pincode = 'Enter valid 6-digit PIN code';

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    // In your handleSubmit function, replace the order creation section with this:

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!validateForm()) return;

        if (cartItems.length === 0) {
            alert('Your cart is empty!');
            return;
        }

        setLoading(true);

        try {
            // Debug: Log all the data we're about to send
            const orderPayload = {
                customerInfo: {
                    firstName: formData.firstName,
                    lastName: formData.lastName,
                    email: formData.email,
                    phone: formData.phone,
                    address: formData.address,
                    city: formData.city,
                    state: formData.state,
                    pincode: formData.pincode
                },
                paymentMethod: formData.paymentMethod
            };

            console.log("=== DEBUG: Order Creation ===");
            console.log("Token exists:", !!token);
            console.log("Cart items count:", cartItems.length);
            console.log("Order payload:", JSON.stringify(orderPayload, null, 2));
            console.log("API URL:", `${process.env.REACT_APP_API_URL}/api/orders/create`);

            // Step 1: Create order with enhanced error handling
            const orderResponse = await fetch(`${process.env.REACT_APP_API_URL}/api/orders/create`, {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(orderPayload)
            });

            console.log("Order response status:", orderResponse.status);
            console.log("Order response headers:", Object.fromEntries(orderResponse.headers.entries()));

            // Enhanced error handling with detailed response
            if (!orderResponse.ok) {
                let errorData;
                const contentType = orderResponse.headers.get('content-type');

                if (contentType && contentType.includes('application/json')) {
                    try {
                        errorData = await orderResponse.json();
                        console.log("Error response body:", errorData);
                    } catch (parseError) {
                        console.log("Failed to parse error response as JSON:", parseError);
                        errorData = { message: 'Failed to parse server error response' };
                    }
                } else {
                    // Try to get text response for non-JSON errors
                    try {
                        const errorText = await orderResponse.text();
                        console.log("Error response text:", errorText);
                        errorData = { message: errorText || 'Server error occurred' };
                    } catch (textError) {
                        console.log("Failed to get error response text:", textError);
                        errorData = { message: 'Unknown server error' };
                    }
                }

                // Throw specific error based on status code
                if (orderResponse.status === 400) {
                    throw new Error(`Validation Error: ${errorData.message || 'Invalid request data'}`);
                } else if (orderResponse.status === 401) {
                    throw new Error('Authentication failed. Please login again.');
                } else if (orderResponse.status === 500) {
                    throw new Error(`Server Error: ${errorData.message || 'Internal server error occurred'}`);
                } else {
                    throw new Error(`Request failed (${orderResponse.status}): ${errorData.message || 'Unknown error'}`);
                }
            }

            const orderData = await orderResponse.json();
            console.log("Order created successfully:", orderData);

            // Validate the response structure
            if (!orderData || !orderData.order || !orderData.order._id) {
                console.error("Invalid order response structure:", orderData);
                throw new Error('Invalid response from server - missing order ID');
            }

            // Step 2: Create payment intent with better error handling
            console.log("=== DEBUG: Payment Intent Creation ===");
            const paymentPayload = {
                orderId: orderData.order._id,
                paymentMethod: formData.paymentMethod
            };
            console.log("Payment payload:", paymentPayload);

            const paymentResponse = await fetch(`${process.env.REACT_APP_API_URL}/api/orders/payment-intent`, {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(paymentPayload)
            });

            console.log("Payment response status:", paymentResponse.status);

            if (!paymentResponse.ok) {
                const errorData = await paymentResponse.json().catch(() => ({ message: 'Failed to create payment intent' }));
                console.log("Payment intent error:", errorData);
                throw new Error(errorData.message || 'Failed to create payment intent');
            }

            const paymentData = await paymentResponse.json();
            console.log("Payment intent created:", paymentData);

            // Step 3: Handle payment based on method
            if (formData.paymentMethod === 'card') {
                console.log("Processing card payment...");

                const successResponse = await fetch(`${process.env.REACT_APP_API_URL}/api/orders/payment-success`, {
                    method: 'POST',
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify({
                        orderId: orderData.order._id,
                        paymentIntentId: paymentData.paymentIntent?.id || 'simulated_payment_id'
                    })
                });

                if (!successResponse.ok) {
                    const errorData = await successResponse.json().catch(() => ({}));
                    throw new Error(`Payment confirmation failed: ${errorData.message || 'Unknown error'}`);
                }

            } else {
                // For UPI/NetBanking
                if (paymentData.paymentUrl) {
                    console.log("Redirecting to payment gateway:", paymentData.paymentUrl);
                    window.location.href = paymentData.paymentUrl;
                    return;
                } else {
                    console.log("Simulating successful UPI/NetBanking payment...");

                    const successResponse = await fetch(`${process.env.REACT_APP_API_URL}/api/orders/payment-success`, {
                        method: 'POST',
                        headers: {
                            'Authorization': `Bearer ${token}`,
                            'Content-Type': 'application/json'
                        },
                        body: JSON.stringify({
                            orderId: orderData.order._id,
                            paymentIntentId: `${formData.paymentMethod}_${Date.now()}`
                        })
                    });

                    if (!successResponse.ok) {
                        const errorData = await successResponse.json().catch(() => ({}));
                        throw new Error(`Payment confirmation failed: ${errorData.message || 'Unknown error'}`);
                    }
                }
            }

            // Clear cart after successful payment
            console.log("Clearing cart...");
            try {
                const clearResponse = await fetch(`${process.env.REACT_APP_API_URL}/api/cart/clear`, {
                    method: 'DELETE',
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Content-Type': 'application/json'
                    }
                });

                if (!clearResponse.ok) {
                    console.warn("Failed to clear cart, but payment was successful");
                } else {
                    console.log("Cart cleared successfully");
                }
            } catch (clearError) {
                console.warn("Error clearing cart:", clearError);
                // Don't throw here since payment was successful
            }

            // Success!
            console.log("=== ORDER COMPLETED SUCCESSFULLY ===");
            alert('Order placed successfully! ðŸŽ‰');
            navigate(`/order-success/${orderData.order._id}`);

        } catch (error) {
            console.error('=== PAYMENT ERROR ===');
            console.error('Error type:', error.constructor.name);
            console.error('Error message:', error.message);
            console.error('Error stack:', error.stack);

            // Show user-friendly error message
            let userMessage = 'Payment failed. Please try again.';

            if (error.message.includes('Authentication')) {
                userMessage = 'Please login again to continue.';
                // Optionally redirect to login
                // navigate('/login');
            } else if (error.message.includes('Validation')) {
                userMessage = 'Please check your form data and try again.';
            } else if (error.message.includes('Server Error')) {
                userMessage = 'Server is experiencing issues. Please try again in a few minutes.';
            }

            alert(`${userMessage}\n\nTechnical details: ${error.message}`);
        } finally {
            setLoading(false);
        }
    };

    // Show loading state
    if (pageLoading) {
        return (
            <div className="bg-light min-vh-100 d-flex align-items-center justify-content-center">
                <div className="text-center">
                    <div className="spinner-border text-primary mb-3" role="status">
                        <span className="visually-hidden">Loading...</span>
                    </div>
                    <p>Loading checkout...</p>
                </div>
            </div>
        );
    }

    // Show empty cart message
    if (cartItems.length === 0) {
        return (
            <div className="bg-light min-vh-100 d-flex align-items-center justify-content-center">
                <div className="text-center">
                    <AlertCircle size={64} className="text-warning mb-3" />
                    <h3>Your cart is empty</h3>
                    <p className="text-muted">Add some courses to your cart before checkout.</p>
                    <button
                        className="btn btn-primary"
                        onClick={() => navigate('/courses')}
                    >
                        Browse Courses
                    </button>
                </div>
            </div>
        );
    }

    return (
        <div className="bg-light min-vh-100 py-4">
            <div className="container">
                <div className="row">
                    <div className="col-12">
                        <nav aria-label="breadcrumb" className="mb-4">
                            <ol className="breadcrumb">
                                <li className="breadcrumb-item">
                                    <button
                                        className="btn btn-link p-0 text-decoration-none"
                                        onClick={() => navigate('/')}
                                    >
                                        Home
                                    </button>
                                </li>
                                <li className="breadcrumb-item">
                                    <button
                                        className="btn btn-link p-0 text-decoration-none"
                                        onClick={() => navigate('/cart')}
                                    >
                                        Cart
                                    </button>
                                </li>
                                <li className="breadcrumb-item active">Checkout</li>
                            </ol>
                        </nav>
                    </div>
                </div>

                <div className="row g-4">
                    {/* Main Checkout Form */}
                    <div className="col-lg-8">
                        <div className="card shadow-sm">
                            <div className="card-header bg-primary text-white">
                                <h4 className="mb-0">
                                    <CreditCard className="me-2" size={20} />
                                    Checkout Details
                                </h4>
                            </div>
                            <div className="card-body">
                                <form onSubmit={handleSubmit}>
                                    {/* Contact Information */}
                                    <div className="mb-4">
                                        <h5 className="border-bottom pb-2 mb-3">Contact Information</h5>
                                        <div className="row g-3">
                                            <div className="col-12">
                                                <label className="form-label">Email Address *</label>
                                                <input
                                                    type="email"
                                                    className={`form-control ${errors.email ? 'is-invalid' : ''}`}
                                                    name="email"
                                                    value={formData.email}
                                                    onChange={handleInputChange}
                                                    placeholder="your.email@example.com"
                                                />
                                                {errors.email && <div className="invalid-feedback">{errors.email}</div>}
                                            </div>
                                        </div>
                                    </div>

                                    {/* Shipping Information */}
                                    <div className="mb-4">
                                        <h5 className="border-bottom pb-2 mb-3">Shipping Information</h5>
                                        <div className="row g-3">
                                            <div className="col-md-6">
                                                <label className="form-label">First Name *</label>
                                                <input
                                                    type="text"
                                                    className={`form-control ${errors.firstName ? 'is-invalid' : ''}`}
                                                    name="firstName"
                                                    value={formData.firstName}
                                                    onChange={handleInputChange}
                                                />
                                                {errors.firstName && <div className="invalid-feedback">{errors.firstName}</div>}
                                            </div>
                                            <div className="col-md-6">
                                                <label className="form-label">Last Name *</label>
                                                <input
                                                    type="text"
                                                    className={`form-control ${errors.lastName ? 'is-invalid' : ''}`}
                                                    name="lastName"
                                                    value={formData.lastName}
                                                    onChange={handleInputChange}
                                                />
                                                {errors.lastName && <div className="invalid-feedback">{errors.lastName}</div>}
                                            </div>
                                            <div className="col-12">
                                                <label className="form-label">Mobile Number *</label>
                                                <input
                                                    type="tel"
                                                    className={`form-control ${errors.phone ? 'is-invalid' : ''}`}
                                                    name="phone"
                                                    value={formData.phone}
                                                    onChange={handleInputChange}
                                                    placeholder="9876543210"
                                                    maxLength="10"
                                                />
                                                {errors.phone && <div className="invalid-feedback">{errors.phone}</div>}
                                            </div>
                                            <div className="col-12">
                                                <label className="form-label">Complete Address *</label>
                                                <textarea
                                                    className={`form-control ${errors.address ? 'is-invalid' : ''}`}
                                                    name="address"
                                                    value={formData.address}
                                                    onChange={handleInputChange}
                                                    rows="3"
                                                    placeholder="House/Flat No., Building Name, Street, Area"
                                                ></textarea>
                                                {errors.address && <div className="invalid-feedback">{errors.address}</div>}
                                            </div>
                                            <div className="col-md-4">
                                                <label className="form-label">City *</label>
                                                <input
                                                    type="text"
                                                    className={`form-control ${errors.city ? 'is-invalid' : ''}`}
                                                    name="city"
                                                    value={formData.city}
                                                    onChange={handleInputChange}
                                                />
                                                {errors.city && <div className="invalid-feedback">{errors.city}</div>}
                                            </div>
                                            <div className="col-md-4">
                                                <label className="form-label">State *</label>
                                                <select
                                                    className={`form-select ${errors.state ? 'is-invalid' : ''}`}
                                                    name="state"
                                                    value={formData.state}
                                                    onChange={handleInputChange}
                                                >
                                                    <option value="">Select State</option>
                                                    {indianStates.map(state => (
                                                        <option key={state} value={state}>{state}</option>
                                                    ))}
                                                </select>
                                                {errors.state && <div className="invalid-feedback">{errors.state}</div>}
                                            </div>
                                            <div className="col-md-4">
                                                <label className="form-label">PIN Code *</label>
                                                <input
                                                    type="text"
                                                    className={`form-control ${errors.pincode ? 'is-invalid' : ''}`}
                                                    name="pincode"
                                                    value={formData.pincode}
                                                    onChange={handleInputChange}
                                                    placeholder="400001"
                                                    maxLength="6"
                                                />
                                                {errors.pincode && <div className="invalid-feedback">{errors.pincode}</div>}
                                            </div>
                                        </div>
                                    </div>

                                    {/* Payment Method */}
                                    <div className="mb-4">
                                        <h5 className="border-bottom pb-2 mb-3">Payment Method</h5>
                                        <div className="row g-3">
                                            <div className="col-md-4">
                                                <div className="form-check border rounded p-3 h-100">
                                                    <input
                                                        type="radio"
                                                        className="form-check-input"
                                                        name="paymentMethod"
                                                        value="card"
                                                        checked={formData.paymentMethod === 'card'}
                                                        onChange={handleInputChange}
                                                    />
                                                    <label className="form-check-label">
                                                        <strong>Credit/Debit Card</strong><br />
                                                        <small className="text-muted">Visa, Mastercard, RuPay</small>
                                                    </label>
                                                </div>
                                            </div>
                                            <div className="col-md-4">
                                                <div className="form-check border rounded p-3 h-100">
                                                    <input
                                                        type="radio"
                                                        className="form-check-input"
                                                        name="paymentMethod"
                                                        value="upi"
                                                        checked={formData.paymentMethod === 'upi'}
                                                        onChange={handleInputChange}
                                                    />
                                                    <label className="form-check-label">
                                                        <strong>UPI</strong><br />
                                                        <small className="text-muted">GPay, PhonePe, Paytm</small>
                                                    </label>
                                                </div>
                                            </div>
                                            <div className="col-md-4">
                                                <div className="form-check border rounded p-3 h-100">
                                                    <input
                                                        type="radio"
                                                        className="form-check-input"
                                                        name="paymentMethod"
                                                        value="netbanking"
                                                        checked={formData.paymentMethod === 'netbanking'}
                                                        onChange={handleInputChange}
                                                    />
                                                    <label className="form-check-label">
                                                        <strong>Net Banking</strong><br />
                                                        <small className="text-muted">All major banks</small>
                                                    </label>
                                                </div>
                                            </div>
                                        </div>
                                    </div>

                                    {/* Security Notice */}
                                    <div className="alert alert-info d-flex align-items-center mb-4">
                                        <Shield className="me-2 flex-shrink-0" size={20} />
                                        <div>
                                            <strong>Secure Payment</strong><br />
                                            Your payment information is encrypted and secure. We never store your card details.
                                        </div>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>

                    {/* Order Summary */}
                    <div className="col-lg-4">
                        <div className="card shadow-sm sticky-top" style={{ top: '20px' }}>
                            <div className="card-header bg-success text-white">
                                <h5 className="mb-0">Order Summary</h5>
                            </div>
                            <div className="card-body">
                                {/* Cart Items */}
                                <div className="mb-3">
                                    {cartItems.map(item => (
                                        <div key={item._id} className="d-flex align-items-center mb-3 pb-3 border-bottom">
                                            <img
                                                src={item.image}
                                                className="rounded me-3"
                                                alt={item.title}
                                                width="60"
                                                height="60"
                                                style={{ objectFit: 'cover' }}
                                                onError={(e) => {
                                                    e.target.src = "https://images.unsplash.com/photo-1633356122544-f134324a6cee?w=150&h=100&fit=crop";
                                                }}
                                            />
                                            <div className="flex-grow-1">
                                                <h6 className="mb-1">{item.title}</h6>
                                                <small className="text-muted">by {item.instructor}</small><br />
                                                <small className="text-muted">Qty: {item.quantity}</small>
                                            </div>
                                            <div className="text-end">
                                                <strong>{formatCurrency(item.price * item.quantity)}</strong>
                                            </div>
                                        </div>
                                    ))}
                                </div>

                                {/* Price Breakdown */}
                                <div className="border-top pt-3">
                                    <div className="d-flex justify-content-between mb-2">
                                        <span>Subtotal:</span>
                                        <span>{formatCurrency(subtotal)}</span>
                                    </div>
                                    <div className="d-flex justify-content-between mb-2">
                                        <span>Shipping:</span>
                                        <span className={shipping === 0 ? 'text-success' : ''}>
                                            {shipping === 0 ? 'FREE' : formatCurrency(shipping)}
                                        </span>
                                    </div>
                                    <div className="d-flex justify-content-between mb-2">
                                        <span>GST (18%):</span>
                                        <span>{formatCurrency(tax)}</span>
                                    </div>
                                    <hr />
                                    <div className="d-flex justify-content-between mb-3">
                                        <strong>Total:</strong>
                                        <strong className="text-primary fs-5">{formatCurrency(total)}</strong>
                                    </div>

                                    {/* Delivery Info */}
                                    <div className="bg-light rounded p-3 mb-3">
                                        <div className="d-flex align-items-center mb-2">
                                            <Truck className="me-2 text-success" size={16} />
                                            <small><strong>Free Delivery</strong> on orders above â‚¹50,000</small>
                                        </div>
                                        <div className="d-flex align-items-center">
                                            <Clock className="me-2 text-info" size={16} />
                                            <small>Delivery in 3-5 business days</small>
                                        </div>
                                    </div>

                                    {/* Place Order Button */}
                                    <button
                                        type="submit"
                                        className="btn btn-primary btn-lg w-100"
                                        disabled={loading}
                                        onClick={handleSubmit}
                                    >
                                        {loading ? (
                                            <>
                                                <span className="spinner-border spinner-border-sm me-2"></span>
                                                Processing...
                                            </>
                                        ) : (
                                            `Place Order - ${formatCurrency(total)}`
                                        )}
                                    </button>

                                    <div className="text-center mt-3">
                                        <small className="text-muted">
                                            By placing this order, you agree to our
                                            <button
                                                className="btn btn-link p-0 text-decoration-none"
                                                onClick={() => navigate('/terms')}
                                            >
                                                {' '}Terms & Conditions
                                            </button>
                                        </small>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                {/* Trust Indicators */}
                <div className="row mt-5">
                    <div className="col-12">
                        <div className="card bg-light">
                            <div className="card-body">
                                <div className="row text-center">
                                    <div className="col-md-3">
                                        <Shield className="text-primary mb-2" size={32} />
                                        <h6>Secure Payment</h6>
                                        <small className="text-muted">SSL encrypted transactions</small>
                                    </div>
                                    <div className="col-md-3">
                                        <Truck className="text-success mb-2" size={32} />
                                        <h6>Fast Delivery</h6>
                                        <small className="text-muted">Pan-India shipping</small>
                                    </div>
                                    <div className="col-md-3">
                                        <Clock className="text-info mb-2" size={32} />
                                        <h6>24/7 Support</h6>
                                        <small className="text-muted">Customer care available</small>
                                    </div>
                                    <div className="col-md-3">
                                        <AlertCircle className="text-warning mb-2" size={32} />
                                        <h6>Easy Returns</h6>
                                        <small className="text-muted">7-day return policy</small>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Checkout;
