// src/pages/Home.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Home = () => {
    const [courses, setCourses] = useState([]);

    useEffect(() => {
        const fetchCourses = async () => {
            try {
                const res = await axios.get('http://localhost:5000/api/admin/courses'); // use your backend route here
                setCourses(res.data);
            } catch (error) {
                console.error('Error fetching courses:', error);
            }
        };

        fetchCourses();
    }, []);

    return (
        <div className="p-4">
            <h1 className="text-2xl font-bold mb-4">All Courses</h1>
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
                {courses.map(course => (
                    <div key={course._id} className="border p-4 rounded shadow">
                        <img src={course.thumbnailUrl} alt={course.title} className="w-full h-40 object-cover mb-2 rounded" />
                        <h2 className="text-lg font-semibold">{course.title}</h2>
                        <p className="text-sm text-gray-600">{course.description.slice(0, 80)}...</p>
                        <p className="text-sm mt-2"><strong>Instructor:</strong> {course.instructor}</p>
                        <p className="text-sm"><strong>Price:</strong> â‚¹{course.price}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Home;
