import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

function CourseDetail() {
    const { id } = useParams();
    const navigate = useNavigate();
    const [course, setCourse] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchCourse = async () => {
            try {
                const res = await axios.get(`http://localhost:5000/api/admin/courses/${id}`);
                setCourse(res.data);
            } catch (err) {
                console.error("Error fetching course:", err);
            } finally {
                setLoading(false);
            }
        };

        fetchCourse();
    }, [id]);

    if (loading) return <h3 className="text-center mt-5">Loading course...</h3>;

    if (!course) return <h3 className="text-center mt-5 text-danger">Course not found!</h3>;

    return (
        <div className="container mt-5">
            <h2 className="mb-3">{course.title}</h2>
            <img
                src={course.thumbnailUrl}
                alt={course.title}
                className="img-fluid mb-4"
                style={{ maxHeight: "400px", objectFit: "cover", borderRadius: "10px" }}
            />
            <p><strong>Instructor:</strong> {course.instructor || "N/A"}</p>
            <p><strong>Category:</strong> {course.category || "N/A"}</p>
            <p><strong>Duration:</strong> {course.duration || "N/A"}</p>
            <p className="mt-3">{course.description}</p>
            <p><strong>Price:</strong> {course.price === 0 ? "Free" : `₹${course.price}`}</p>

            {course.price > 0 && (
                <button
                    className="btn btn-success mt-3"
                    onClick={() => navigate(`/checkout/${course._id}`)}
                >
                    Buy Now
                </button>
            )}
        </div>
    );
}

export default CourseDetail;
