/* eslint-disable react-hooks/exhaustive-deps */
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import CourseCard from '../../components/CourseCard';

const Favourite = () => {
  const [favoriteCourses, setFavoriteCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [toast, setToast] = useState({ show: false, message: '', type: '' });

  // Toast function to show notifications
  const showToast = (message, type = 'info') => {
    setToast({ show: true, message, type });
    setTimeout(() => {
      setToast({ show: false, message: '', type: '' });
    }, 3000);
  };

  useEffect(() => {
    loadFavouriteCourses();
  }, []);

  // Listen for localStorage changes (if user opens multiple tabs)
  useEffect(() => {
    const handleStorageChange = (e) => {
      if (e.key === 'userFavourites') {
        loadFavouriteCourses();
      }
    };

    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, []);

  const loadFavouriteCourses = async () => {
    try {
      setLoading(true);
      setError(null);

      // Get favourite course IDs from localStorage
      const favoriteIds = JSON.parse(localStorage.getItem('userFavorites') || '[]');
      
      if (favoriteIds.length === 0) {
        setFavouriteCourses([]);
        setLoading(false);
        return;
      }

      // Fetch all courses from your API
      const response = await axios.get("http://localhost:5000/api/courses");
      const allCourses = response.data || [];
      
      // Filter courses to only include favourites
      const favorites = allCourses.filter(course => {
        const courseId = course._id || course.id;
        return favoriteIds.includes(courseId);
      });
      
      setFavouriteCourses(favorites);
    } catch (err) {
      console.error('Error loading favorite courses:', err);
      setError(err.response?.data?.message || 'Failed to load favorite courses');
    } finally {
      setLoading(false);
    }
  };

  const handleFavouriteToggle = (courseId, isAdded) => {
    if (!isAdded) {
      // Course was removed from favourites, update the display immediately
      setFavouriteCourses(prev => prev.filter(course => {
        const id = course._id || course.id;
        return id !== courseId;
      }));
      showToast('Course removed from favorites', 'success');
    } else {
      // Course was added to favourites, reload the list
      loadFavouriteCourses();
      showToast('Course added to favorites', 'success');
    }
  };

  const handleClearAllFavorites = () => {
    if (window.confirm('Are you sure you want to remove all courses from favorites?')) {
      localStorage.setItem('userFavourites', JSON.stringify([]));
      setFavouriteCourses([]);
      showToast('All favorites cleared', 'success');
    }
  };

  const handleRefreshFavorites = () => {
    loadFavoriteCourses();
    showToast('Favorites refreshed', 'info');
  };

  if (loading) {
    return (
      <div className="container-fluid py-4">
        <div className="d-flex justify-content-center align-items-center" style={{ minHeight: '400px' }}>
          <div className="text-center">
            <div className="spinner-border text-primary" role="status" style={{ width: '3rem', height: '3rem' }}>
              <span className="visually-hidden">Loading...</span>
            </div>
            <h4 className="mt-3 text-muted">Loading your favorite courses...</h4>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container-fluid py-4">
        <div className="row justify-content-center">
          <div className="col-12 text-center py-5">
            <div className="mb-4">
              <i className="fas fa-exclamation-triangle fa-4x text-danger opacity-50"></i>
            </div>
            <h4 className="text-danger mb-3">Error Loading Favorites</h4>
            <p className="text-muted mb-4">{error}</p>
            <div className="d-flex gap-2 justify-content-center">
              <button className="btn btn-primary" onClick={loadFavouriteCourses}>
                <i className="fas fa-refresh me-2"></i>
                Try Again
              </button>
              <Link to="/courses" className="btn btn-outline-secondary">
                <i className="fas fa-arrow-left me-2"></i>
                Browse Courses
              </Link>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid py-4">
      {/* Toast Notification */}
      {toast.show && (
        <div className={`toast show position-fixed top-0 end-0 m-3`} style={{ zIndex: 1050 }}>
          <div className={`toast-header bg-${toast.type === 'success' ? 'success' : toast.type === 'error' ? 'danger' : 'info'} text-white`}>
            <strong className="me-auto">
              <i className={`fas fa-${toast.type === 'success' ? 'check-circle' : toast.type === 'error' ? 'exclamation-circle' : 'info-circle'} me-2`}></i>
              Notification
            </strong>
            <button 
              type="button" 
              className="btn-close btn-close-white" 
              onClick={() => setToast({ show: false, message: '', type: '' })}
            ></button>
          </div>
          <div className="toast-body text-dark">
            {toast.message}
          </div>
        </div>
      )}

      {/* Page Header */}
      <div className="row mb-4">
        <div className="col-12">
          <div className="d-flex align-items-center justify-content-between">
            <div>
              <h2 className="mb-2">
                <i className="bi bi-heart-fill text-danger me-2"></i>
                My Favourite Courses
              </h2>
              <p className="text-muted mb-0">
                {favouriteCourses.length > 0 
                  ? `You have ${favouriteCourses.length} favourite course${favouriteCourses.length !== 1 ? 's' : ''}`
                  : 'Your favourite courses will appear here'
                }
              </p>
            </div>
            {favouriteCourses.length > 0 && (
              <div className="d-flex gap-2">
                <button 
                  className="btn btn-outline-primary btn-sm"
                  onClick={handleRefreshFavourites}
                  title="Refresh favourites"
                >
                  <i className="fas fa-refresh me-1"></i>
                  Refresh
                </button>
                <button 
                  className="btn btn-outline-danger btn-sm"
                  onClick={handleClearAllFavourites}
                  title="Clear all favourites"
                >
                  <i className="fas fa-trash me-1"></i>
                  Clear All
                </button>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Favourite Courses Grid */}
      {favouriteCourses.length > 0 ? (
        <div className="row g-4">
          {favouriteCourses.map((course) => (
            <div 
              key={course._id || course.id} 
              className="col-12 col-sm-6 col-md-4 col-lg-3"
            >
              <CourseCard 
                course={course} 
                onFavouriteToggle={handleFavouriteToggle}
                showToast={showToast}
              />
            </div>
          ))}
        </div>
      ) : (
        /* Empty State */
        <div className="row">
          <div className="col-12">
            <div className="text-center py-5">
              <div className="mb-4">
                <i 
                  className="bi bi-heart text-muted" 
                  style={{ fontSize: '4rem' }}
                ></i>
              </div>
              <h4 className="text-muted mb-3">No favourite courses yet</h4>
              <p className="text-muted mb-4">
                Start exploring our courses and click the heart icon to add them to your favourites!
              </p>
              <div className="d-flex flex-column flex-sm-row gap-3 justify-content-center">
                <Link to="/courses" className="btn btn-primary">
                  <i className="fas fa-search me-2"></i>
                  Browse Courses
                </Link>
                <Link to="/" className="btn btn-outline-secondary">
                  <i className="bi bi-house me-2"></i>
                  Back to Home
                </Link>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Quick Stats */}
      {favouriteCourses.length > 0 && (
        <div className="row mt-5">
          <div className="col-12">
            <div className="card bg-light">
              <div className="card-body">
                <div className="row text-center">
                  <div className="col-6 col-md-3">
                    <div className="fw-bold text-primary fs-4">
                      {favouriteCourses.length}
                    </div>
                    <small className="text-muted">Favourite Courses</small>
                  </div>
                  <div className="col-6 col-md-3">
                    <div className="fw-bold text-success fs-4">
                      {favouriteCourses.filter(course => course.price === 0 || course.isFree).length}
                    </div>
                    <small className="text-muted">Free Courses</small>
                  </div>
                  <div className="col-6 col-md-3">
                    <div className="fw-bold text-warning fs-4">
                      {favouriteCourses.filter(course => course.price > 0 && !course.isFree).length}
                    </div>
                    <small className="text-muted">Premium Courses</small>
                  </div>
                  <div className="col-6 col-md-3">
                    <div className="fw-bold text-info fs-4">
                      â‚¹{favouriteCourses
                        .filter(course => course.price > 0 && !course.isFree)
                        .reduce((total, course) => total + (course.price || 0), 0)
                        .toLocaleString()}
                    </div>
                    <small className="text-muted">Total Value</small>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Back to Courses Button */}
      {favouriteCourses.length > 0 && (
        <div className="row mt-4">
          <div className="col-12 text-center">
            <Link to="/courses" className="btn btn-outline-primary">
              <i className="fas fa-arrow-left me-2"></i>
              Continue Browsing Courses
            </Link>
          </div>
        </div>
      )}
    </div>
  );
};

export default Favourite;
