import React, { useEffect, useState } from "react";
import axios from "axios";
import CourseCard from "../components/CourseCard";

function Courses() {
    const [allCourses, setAllCourses] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState("All");
    const [filter, setFilter] = useState("All");
    const [showAllCategories, setShowAllCategories] = useState(false);
    const [categories, setCategories] = useState([]);

    // Fetch courses from backend
    useEffect(() => {
        const fetchCourses = async () => {
            try {
                const res = await axios.get("http://localhost:5000/api/admin/courses");
                setAllCourses(res.data);

                // Extract unique categories from courses
                const uniqueCategories = [
                    ...new Set(res.data.map((course) => course.category || "Uncategorized")),
                ];
                setCategories(uniqueCategories);
            } catch (err) {
                console.error("Error fetching courses:", err);
            }
        };

        fetchCourses();
    }, []);

    // Apply category filter
    let filteredCourses =
        selectedCategory === "All"
            ? allCourses
            : allCourses.filter((course) => course.category === selectedCategory);

    // Apply price filter
    filteredCourses = filteredCourses.filter((course) => {
        if (filter === "Free") return course.price === 0;
        if (filter === "Paid") return course.price > 0;
        return true;
    });

    const visibleCategories = showAllCategories ? categories : categories.slice(0, 10);
    const hiddenCount = categories.length - 10;

    return (
        <div className="container my-5">
            <h2 className="mb-4 text-center">Courses</h2>

            {/* Category Filter */}
            <div className="d-flex flex-wrap justify-content-center mb-3">
                <button
                    className={`btn btn-outline-primary m-2 ${selectedCategory === "All" ? "active" : ""}`}
                    onClick={() => setSelectedCategory("All")}
                >
                    All
                </button>
                {visibleCategories.map((cat) => (
                    <button
                        key={cat}
                        className={`btn btn-outline-primary m-2 ${selectedCategory === cat ? "active" : ""}`}
                        onClick={() => setSelectedCategory(cat)}
                    >
                        {cat}
                    </button>
                ))}
                {!showAllCategories && hiddenCount > 0 && (
                    <button
                        className="btn btn-outline-primary m-2"
                        onClick={() => setShowAllCategories(true)}
                    >
                        +{hiddenCount} more
                    </button>
                )}
                {showAllCategories && hiddenCount > 0 && (
                    <button
                        className="btn btn-outline-primary m-2"
                        onClick={() => setShowAllCategories(false)}
                    >
                        Show Less
                    </button>
                )}
            </div>

            {/* Price Filter */}
            <div className="container mb-4 d-flex justify-content-end">
                <select
                    className="form-select w-auto"
                    value={filter}
                    onChange={(e) => setFilter(e.target.value)}
                >
                    <option value="All">All Courses</option>
                    <option value="Free">Free</option>
                    <option value="Paid">Paid</option>
                </select>
            </div>

            {/* Course Cards */}
            <div className="row row-cols-1 row-cols-md-3 g-4">
                {filteredCourses.length > 0 ? (
                    filteredCourses.map((course) => (
                        <div key={course._id} className="col">
                            <CourseCard course={course} />
                        </div>
                    ))
                ) : (
                    <p className="text-center">No courses found.</p>
                )}
            </div>
        </div>
    );
}

export default Courses;
