import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

function CourseDetail() {
  const { id } = useParams();
  const [course, setCourse] = useState(null);

  useEffect(() => {
    const fetchCourse = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/admin/courses/${id}`);
        setCourse(res.data);
      } catch (err) {
        console.error("Error fetching course:", err);
      }
    };

    fetchCourse();
  }, [id]);

  if (!course) return <h3 className="text-center mt-5">Loading course...</h3>;

  return (
    <div className="container mt-5">
      <h2>{course.title}</h2>
      <img
        src={course.thumbnailUrl}
        alt={course.title}
        style={{ width: "100%", maxWidth: "600px", borderRadius: "8px" }}
      />
      <p className="mt-3"><strong>Instructor:</strong> {course.instructor || "N/A"}</p>
      <p><strong>Category:</strong> {course.category || "N/A"}</p>
      <p><strong>Duration:</strong> {course.duration || "N/A"}</p>
      <p className="mt-2">{course.description}</p>
      <p><strong>Price:</strong> {course.price === 0 ? "Free" : `₹${course.price}`}</p>
    </div>
  );
}

export default CourseDetail;
