// src/components/CourseCard.jsx
import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./CourseCard.css";

function CourseCard({ course, onFavouriteToggle, showToast }) {
  const [isFavorite, setIsFavorite] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  // Get the course ID - handle both MongoDB _id and regular id
  const getCourseId = () => {
    return course._id || course.id;
  };

  // Check if user is authenticated
  const isUserAuthenticated = () => {
    const token = localStorage.getItem('authToken') || localStorage.getItem('token');
    const userData = localStorage.getItem('userData') || localStorage.getItem('user');
    return !!(token || userData);
  };

  // Get auth token for API calls
  const getAuthToken = () => {
    return localStorage.getItem('authToken') || localStorage.getItem('token');
  };

  // Check favorite status from backend
  const checkFavoriteStatus = async () => {
    try {
      if (!isUserAuthenticated()) {
        setIsFavorite(false);
        return;
      }

      const courseId = getCourseId();
      if (!courseId) return;

      const token = getAuthToken();
      const response = await fetch(`http://localhost:5000/api/favorites/check/${courseId}`, {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      });

      if (response.ok) {
        const data = await response.json();
        setIsFavorite(data.isFavorite);
      }
    } catch (error) {
      console.error('Error checking favorite status:', error);
    }
  };

  useEffect(() => {
    checkFavoriteStatus();
  }, [course._id, course.id]);

  const handleFavoriteToggle = async (e) => {
    e.preventDefault();
    e.stopPropagation();

    // Check if user is authenticated first
    if (!isUserAuthenticated()) {
      if (showToast) {
        showToast('Please login to add courses to favorites', 'info');
      }
      
      setTimeout(() => {
        navigate('/login');
      }, 1500);
      
      return;
    }

    try {
      setIsLoading(true);
      const courseId = getCourseId();
      
      if (!courseId) {
        console.error('No valid course ID found');
        if (showToast) {
          showToast('Error: Course ID not found', 'error');
        }
        return;
      }

      const token = getAuthToken();
      let response;
      let message;

      if (isFavorite) {
        // Remove from favorites
        response = await fetch(`http://localhost:5000/api/favorites/${courseId}`, {
          method: 'DELETE',
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        });
        message = 'Removed from favorites';
      } else {
        // Add to favorites
        response = await fetch('http://localhost:5000/api/favorites', {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ courseId }),
        });
        message = 'Added to favorites';
      }

      if (response.ok) {
        setIsFavorite(!isFavorite);
        
        if (showToast) {
          showToast(message, 'success');
        }

        // Call parent callback if provided
        if (onFavouriteToggle) {
          onFavouriteToggle(courseId, !isFavorite);
        }
      } else {
        const errorData = await response.json();
        
        if (response.status === 409) {
          // Course already in favorites - sync the state
          setIsFavorite(true);
          if (showToast) {
            showToast('Course already in favorites', 'info');
          }
        } else {
          throw new Error(errorData.message || 'Failed to update favorites');
        }
      }
    } catch (error) {
      console.error('Error toggling favorite:', error);
      
      if (showToast) {
        showToast(error.message || 'Error updating favorites', 'error');
      }
    } finally {
      setIsLoading(false);
    }
  };

  // Handle image URL - use thumbnailUrl from backend if available, otherwise fallback to image
  const getImageUrl = () => {
    if (course.thumbnailUrl) {
      return `http://localhost:5000${course.thumbnailUrl}`;
    }
    return course.image || '/default-course-image.jpg';
  };

  // Get price display text
  const getPriceDisplay = () => {
    if (course.isFree) {
      return "Free";
    }
    
    const price = course.price || 0;
    return price === 0 ? "Free" : `â‚¹${price}`;
  };

  const courseId = getCourseId();

  // If no valid course ID, show error state
  if (!courseId) {
    console.error('Course missing ID:', course);
    return (
      <div className="card shadow-sm course-card">
        <div className="card-body">
          <h5 className="card-title text-danger">Error: Invalid Course Data</h5>
          <p className="card-text">Course ID is missing or invalid.</p>
        </div>
      </div>
    );
  }

  const isAuthenticated = isUserAuthenticated();

  return (
    <div className="card shadow-sm course-card">
      <div className="course-image-wrapper">
        <img
          src={getImageUrl()}
          className="card-img-top"
          alt={course.title}
          style={{ height: "180px", objectFit: "cover" }}
        />
      </div>
      <div className="card-body d-flex flex-column">
        <h5 className="card-title">{course.title?.slice(0, 50) || "Untitled Course"}</h5>
        <p className="card-text">
          {course.description?.slice(0, 100) || "No description available"}...
        </p>

        {/* Show instructor if available from backend */}
        {course.instructor && (
          <p className="text-muted mb-2">
            <strong>Instructor:</strong> {course.instructor}
          </p>
        )}

        <p className="text-success mb-3">
          {getPriceDisplay()}
        </p>

        <div className="mt-auto position-relative">
          <Link to={`/courses/${courseId}`} className="btn btn-primary w-100">
            View Course
          </Link>
          {/* Favorite Button */}
          <button
            className={`favorite-btn ${isFavorite ? 'favorite-active' : ''} ${!isAuthenticated ? 'favorite-disabled' : ''}`}
            onClick={handleFavoriteToggle}
            disabled={isLoading}
            title={
              !isAuthenticated 
                ? 'Login to add to favorites' 
                : isLoading
                  ? 'Loading...'
                  : isFavorite 
                    ? 'Remove from favorites' 
                    : 'Add to favorites'
            }
            aria-label={
              !isAuthenticated 
                ? 'Login to add to favorites' 
                : isLoading
                  ? 'Loading...'
                  : isFavorite 
                    ? 'Remove from favorites' 
                    : 'Add to favorites'
            }
          >
            {isLoading ? (
              <i className="bi bi-arrow-repeat spin"></i>
            ) : (
              <i className={`bi ${isFavorite ? 'bi-heart-fill' : 'bi-heart'}`}></i>
            )}
          </button>
        </div>
      </div>
    </div>
  );
}

export default CourseCard;
