import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

export default function AdminLogin() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');
    const navigate = useNavigate();

    // ✅ Check admin token validity before redirect
    useEffect(() => {
        const verifyAdminToken = async () => {
            const token = localStorage.getItem('adminToken');
            if (!token) return;

            try {
                const res = await fetch('http://localhost:5000/verify-admin-token', {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });

                const data = await res.json();

                if (res.status === 200 && data.role === 'admin') {
                    navigate('/admin-dashboard');
                } else {
                    localStorage.removeItem('adminToken'); // invalid token
                }
            } catch (err) {
                localStorage.removeItem('adminToken'); // on error, clear token
            }
        };

        verifyAdminToken();
    }, [navigate]);

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const res = await fetch('http://localhost:5000/admin-login', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ email, password }),
            });

            const data = await res.json();

            if (res.status === 200) {
                localStorage.setItem('adminToken', data.token);
                navigate('/admin-dashboard');
            } else {
                setMessage(data.message || 'Login failed');
            }
        } catch (err) {
            setMessage('Server error');
        }
    };

    return (
        <div className="container d-flex justify-content-center align-items-center min-vh-100">
            <div className="card p-4 shadow" style={{ maxWidth: '400px', width: '100%' }}>
                <h2 className="text-center mb-4">Admin Login</h2>
                <form onSubmit={handleSubmit}>
                    <div className="mb-3">
                        <input
                            type="email"
                            className="form-control"
                            placeholder="Admin Email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                            autoFocus
                        />
                    </div>
                    <div className="mb-3">
                        <input
                            type="password"
                            className="form-control"
                            placeholder="Password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </div>
                    <button type="submit" className="btn btn-danger w-100">
                        Login as Admin
                    </button>
                </form>
                {message && <p className="mt-3 text-center text-danger">{message}</p>}
            </div>
        </div>
    );
}
