/* eslint-disable react-hooks/exhaustive-deps */
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import CourseCard from '../../components/CourseCard';

// Mock course data - replace with your actual course data source
const getAllCourses = () => {
  // This should be replaced with your actual course data fetching logic
  return [
    {
      id: 1,
      title: "React.js Complete Course for Beginners",
      description: "Learn React.js from scratch with hands-on projects and real-world examples. Master components, hooks, state management, and more in this comprehensive course.",
      image: "https://via.placeholder.com/300x180/007bff/ffffff?text=React+Course",
      price: 2999,
      isFree: false
    },
    {
      id: 2,
      title: "JavaScript Fundamentals",
      description: "Master the fundamentals of JavaScript programming language. Learn variables, functions, objects, arrays, and modern ES6+ features.",
      image: "https://via.placeholder.com/300x180/ffc107/000000?text=JavaScript",
      price: 0,
      isFree: true
    },
    {
      id: 3,
      title: "Node.js Backend Development",
      description: "Build powerful backend applications with Node.js, Express.js, and MongoDB. Learn API development, authentication, and deployment.",
      image: "https://via.placeholder.com/300x180/28a745/ffffff?text=Node.js",
      price: 3999,
      isFree: false
    },
    // Add more courses as needed
  ];
};

const Favourite = () => {
  const [favouriteCourses, setFavouriteCourses] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadFavouriteCourses();
  }, []);

  const loadFavouriteCourses = () => {
    try {
      const favouriteIds = JSON.parse(localStorage.getItem('userFavourites') || '[]');
      const allCourses = getAllCourses(); // Replace with your course data source
      
      const favourites = allCourses.filter(course => 
        favouriteIds.includes(course.id)
      );
      
      setFavouriteCourses(favourites);
    } catch (error) {
      console.error('Error loading favourite courses:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleFavouriteToggle = (courseId, isAdded) => {
    // Reload the favourites list after toggle
    loadFavouriteCourses();
  };

  if (loading) {
    return (
      <div className="container-fluid py-4">
        <div className="d-flex justify-content-center align-items-center" style={{ minHeight: '400px' }}>
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid py-4">
      {/* Page Header */}
      <div className="row mb-4">
        <div className="col-12">
          <div className="d-flex align-items-center justify-content-between">
            <div>
              <h2 className="mb-2">
                <i className="bi bi-heart-fill text-danger me-2"></i>
                My Favourite Courses
              </h2>
              <p className="text-muted mb-0">
                {favouriteCourses.length > 0 
                  ? `You have ${favouriteCourses.length} favourite course${favouriteCourses.length !== 1 ? 's' : ''}`
                  : 'Your favourite courses will appear here'
                }
              </p>
            </div>
          </div>
        </div>
      </div>

      {/* Favourite Courses Grid */}
      {favouriteCourses.length > 0 ? (
        <div className="row g-4">
          {favouriteCourses.map((course) => (
            <div 
              key={course.id} 
              className="col-12 col-sm-6 col-md-4 col-lg-3"
            >
              <CourseCard 
                course={course} 
                onFavouriteToggle={handleFavouriteToggle}
              />
            </div>
          ))}
        </div>
      ) : (
        /* Empty State */
        <div className="row">
          <div className="col-12">
            <div className="text-center py-5">
              <div className="mb-4">
                <i 
                  className="bi bi-heart text-muted" 
                  style={{ fontSize: '4rem' }}
                ></i>
              </div>
              <h4 className="text-muted mb-3">No favourite courses yet</h4>
              <p className="text-muted mb-4">
                Start exploring our courses and click the heart icon to add them to your favourites!
              </p>
              <div className="d-flex flex-column flex-sm-row gap-3 justify-content-center">
                <Link to="/" className="btn btn-outline-secondary">
                  <i className="bi bi-house me-2"></i>
                  Back to Home
                </Link>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Quick Stats */}
      {favouriteCourses.length > 0 && (
        <div className="row mt-5">
          <div className="col-12">
            <div className="card bg-light">
              <div className="card-body">
                <div className="row text-center">
                  <div className="col-6 col-md-3">
                    <div className="fw-bold text-primary fs-4">
                      {favouriteCourses.length}
                    </div>
                    <small className="text-muted">Favourite Courses</small>
                  </div>
                  <div className="col-6 col-md-3">
                    <div className="fw-bold text-success fs-4">
                      {favouriteCourses.filter(course => course.isFree).length}
                    </div>
                    <small className="text-muted">Free Courses</small>
                  </div>
                  <div className="col-6 col-md-3">
                    <div className="fw-bold text-warning fs-4">
                      {favouriteCourses.filter(course => !course.isFree).length}
                    </div>
                    <small className="text-muted">Premium Courses</small>
                  </div>
                  <div className="col-6 col-md-3">
                    <div className="fw-bold text-info fs-4">
                      ₹{favouriteCourses
                        .filter(course => !course.isFree)
                        .reduce((total, course) => total + course.price, 0)
                        .toLocaleString()}
                    </div>
                    <small className="text-muted">Total Value</small>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Favourite;
