import React from 'react';
import { useNavigate } from 'react-router-dom';

export default function AdminDashboard() {
    const navigate = useNavigate();

    const handleLogout = () => {
        localStorage.removeItem('adminToken'); // if youâ€™re using separate admin token
        navigate('/admin-login');
    };

    return (
        <div className="container py-5">
            <div className="card shadow p-4">
                <h2 className="text-center mb-4">Admin Dashboard</h2>
                <p>Welcome, Admin! You can manage courses and users from here.</p>

                <div className="d-flex flex-column gap-3 mt-4">
                    <button className="btn btn-outline-primary" onClick={() => navigate('/admin/upload-course')}>
                        ðŸ“¤ Upload Course
                    </button>
                    <button className="btn btn-outline-success" onClick={() => navigate('/admin/view-courses')}>
                        ðŸ“š View All Courses
                    </button>
                    <button className="btn btn-outline-secondary" onClick={() => navigate('/admin/users')}>
                        ðŸ‘¤ Manage Users
                    </button>
                    <button className="btn btn-danger mt-3" onClick={handleLogout}>
                        ðŸ”’ Logout
                    </button>
                </div>
            </div>
        </div>
    );
}
