/* eslint-disable no-unused-vars */
import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  User,
  Book,
  Award,
  Edit3,
  Camera,
  Mail,
  Phone,
  MapPin,
  Calendar,
  GraduationCap,
  Trophy,
  Clock,
  BarChart3,
  Save,
  X,
  Upload,
  AlertCircle,
} from "lucide-react";

export default function Profile() {
  const [activeTab, setActiveTab] = useState("overview");
  const [isEditing, setIsEditing] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [userInfo, setUserInfo] = useState({
    name: "",
    firstName: "",
    lastName: "",
    username: "",
    email: "",
    phone: "",
    location: "",
    joinDate: "",
    bio: "",
    profileImage: "https://images.unsplash.com/photo-1494790108755-2616b612b786?w=150&h=150&fit=crop&crop=face",
    dateOfBirth: "",
    studentId: "",
    major: "Computer Science",
    year: "Senior",
  });

  // Fetch user profile data on component mount
  useEffect(() => {
    fetchUserProfile();
  }, []);

  const fetchUserProfile = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('No authentication token found');
        setLoading(false);
        return;
      }

      const response = await axios.get('http://localhost:5000/api/user/profile', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.data.user) {
        const userData = response.data.user;
        
        // Split username into first and last name if needed
        const nameParts = userData.username.split(' ');
        const firstName = nameParts[0] || userData.username;
        const lastName = nameParts.slice(1).join(' ') || '';
        
        setUserInfo(prev => ({
          ...prev,
          name: userData.username,
          firstName: firstName,
          lastName: lastName,
          username: userData.username,
          email: userData.email,
          joinDate: userData.createdAt ? new Date(userData.createdAt).toLocaleDateString('en-US', { 
            year: 'numeric', 
            month: 'long' 
          }) : 'Unknown',
          // Keep existing values for fields not in backend
          phone: prev.phone,
          location: prev.location,
          bio: prev.bio,
          dateOfBirth: prev.dateOfBirth,
          studentId: prev.studentId,
          major: prev.major,
          year: prev.year,
        }));
      }
      
      setLoading(false);
    } catch (error) {
      console.error('Error fetching profile:', error);
      setError(error.response?.data?.message || 'Failed to fetch profile data');
      setLoading(false);
    }
  };

  const handleInputChange = (field, value) => {
    setUserInfo((prev) => ({
      ...prev,
      [field]: value,
      ...(field === "firstName" || field === "lastName"
        ? {
            name:
              field === "firstName"
                ? `${value} ${prev.lastName}`
                : `${prev.firstName} ${value}`,
            username:
              field === "firstName"
                ? `${value} ${prev.lastName}`
                : `${prev.firstName} ${value}`,
          }
        : {}),
    }));
  };

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setUserInfo((prev) => ({ ...prev, profileImage: e.target.result }));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSave = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('No authentication token found');
        return;
      }

      // Here you would typically send the updated data to your backend
      // For now, we'll just update the local state
      setIsEditing(false);
      setError('');
      
      // TODO: Implement API call to update user profile
      console.log('Profile updated:', userInfo);
    } catch (error) {
      console.error('Error saving profile:', error);
      setError('Failed to save profile changes');
    }
  };

  const handleCancel = () => {
    // Reset any unsaved changes by refetching the profile
    fetchUserProfile();
    setIsEditing(false);
    setError('');
  };

  // Loading state
  if (loading) {
    return (
      <div className="container-fluid bg-light min-vh-100 py-4">
        <div className="container-sm">
          <div className="row justify-content-center">
            <div className="col-12 col-md-6 text-center">
              <div className="spinner-border text-primary" role="status">
                <span className="visually-hidden">Loading...</span>
              </div>
              <p className="mt-3 text-muted">Loading profile...</p>
            </div>
          </div>
        </div>
      </div>
    );
  }

  // Error state
  if (error && !userInfo.email) {
    return (
      <div className="container-fluid bg-light min-vh-100 py-4">
        <div className="container-sm">
          <div className="row justify-content-center">
            <div className="col-12 col-md-6">
              <div className="alert alert-danger d-flex align-items-center" role="alert">
                <AlertCircle className="me-2" size={20} />
                <div>
                  <strong>Error:</strong> {error}
                </div>
              </div>
              <button 
                className="btn btn-primary"
                onClick={fetchUserProfile}
              >
                Retry
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid bg-light min-vh-100 py-4">
      <div className="container-sm">
        {/* Error message */}
        {error && (
          <div className="row mb-3">
            <div className="col-12">
              <div className="alert alert-warning alert-dismissible fade show" role="alert">
                <AlertCircle className="me-2" size={16} />
                {error}
                <button 
                  type="button" 
                  className="btn-close" 
                  onClick={() => setError('')}
                  aria-label="Close"
                ></button>
              </div>
            </div>
          </div>
        )}

        <div className="row mb-4">
          <div className="col-12">
            <div className="card shadow-sm">
              <div className="card-body">
                <div className="row align-items-center">
                  <div className="col-auto">
                    <div className="position-relative">
                      <img
                        src={userInfo.profileImage}
                        alt="Profile"
                        className="rounded-circle img-fluid"
                        style={{ width: "80px", height: "80px", objectFit: "cover" }}
                      />
                      {isEditing ? (
                        <div className="position-absolute bottom-0 end-0">
                          <input
                            type="file"
                            id="profileImageUpload"
                            accept="image/*"
                            onChange={handleImageUpload}
                            className="d-none"
                          />
                          <label
                            htmlFor="profileImageUpload"
                            className="btn btn-primary btn-sm rounded-circle d-flex align-items-center justify-content-center"
                            style={{ width: "35px", height: "35px" }}
                          >
                            <Upload size={16} />
                          </label>
                        </div>
                      ) : (
                        <button className="btn btn-primary btn-sm position-absolute bottom-0 end-0 rounded-circle">
                          <Camera size={14} />
                        </button>
                      )}
                    </div>
                  </div>
                  <div className="col">
                    <div className="d-flex flex-column flex-md-row justify-content-between align-items-start gap-3">
                      <div>
                        {isEditing ? (
                          <div className="mb-3">
                            <div className="row">
                              <div className="col-md-6 mb-2">
                                <input
                                  type="text"
                                  className="form-control"
                                  value={userInfo.firstName}
                                  onChange={(e) => handleInputChange("firstName", e.target.value)}
                                  placeholder="First Name"
                                />
                              </div>
                              <div className="col-md-6 mb-2">
                                <input
                                  type="text"
                                  className="form-control"
                                  value={userInfo.lastName}
                                  onChange={(e) => handleInputChange("lastName", e.target.value)}
                                  placeholder="Last Name"
                                />
                              </div>
                            </div>
                          </div>
                        ) : (
                          <>
                            <h2 className="mb-1 text-break">{userInfo.name}</h2>
                            <p className="text-muted mb-2">{userInfo.major} Student</p>
                          </>
                        )}
                        <div className="d-flex flex-wrap gap-3 text-sm">
                          <span><Mail size={16} className="me-1" />{userInfo.email}</span>
                          <span><MapPin size={16} className="me-1" />{userInfo.location || 'Location not set'}</span>
                          <span><Calendar size={16} className="me-1" />Joined {userInfo.joinDate}</span>
                        </div>
                      </div>
                      {isEditing ? (
                        <div className="d-flex gap-2">
                          <button className="btn btn-success" onClick={handleSave}>
                            <Save size={16} className="me-1" />Save
                          </button>
                          <button className="btn btn-outline-secondary" onClick={handleCancel}>
                            <X size={16} className="me-1" />Cancel
                          </button>
                        </div>
                      ) : (
                        <button className="btn btn-outline-primary" onClick={() => setIsEditing(!isEditing)}>
                          <Edit3 size={16} className="me-1" />Edit Profile
                        </button>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Overview Tab */}
        <div className="row">
          <div className="col-12">
            {activeTab === "overview" && (
              <div className="row">
                <div className="col-12 col-md-6 col-lg-4 mb-4">
                  <div className="card shadow-sm h-100">
                    <div className="card-header">
                      <h5 className="mb-0">About Me</h5>
                    </div>
                    <div className="card-body">
                      {isEditing ? (
                        <textarea
                          className="form-control"
                          rows="4"
                          value={userInfo.bio}
                          onChange={(e) => handleInputChange("bio", e.target.value)}
                          placeholder="Tell us about yourself..."
                        />
                      ) : (
                        <p className="mb-0">{userInfo.bio || 'No bio available. Click Edit Profile to add one.'}</p>
                      )}
                    </div>
                  </div>
                </div>

                <div className="col-12 col-md-6 col-lg-4 mb-4">
                  <div className="card shadow-sm h-100">
                    <div className="card-header">
                      <h5 className="mb-0">Contact Information</h5>
                    </div>
                    <div className="card-body">
                      {isEditing ? (
                        <div className="mb-3">
                          <label className="form-label">Email</label>
                          <input
                            type="email"
                            className="form-control mb-3"
                            value={userInfo.email}
                            onChange={(e) => handleInputChange("email", e.target.value)}
                            disabled // Email should not be editable
                          />
                          <label className="form-label">Phone</label>
                          <input
                            type="tel"
                            className="form-control mb-3"
                            value={userInfo.phone}
                            onChange={(e) => handleInputChange("phone", e.target.value)}
                            placeholder="Enter phone number"
                          />
                          <label className="form-label">Location</label>
                          <input
                            type="text"
                            className="form-control mb-3"
                            value={userInfo.location}
                            onChange={(e) => handleInputChange("location", e.target.value)}
                            placeholder="Enter your location"
                          />
                          <label className="form-label">Date of Birth</label>
                          <input
                            type="date"
                            className="form-control"
                            value={userInfo.dateOfBirth}
                            onChange={(e) => handleInputChange("dateOfBirth", e.target.value)}
                          />
                        </div>
                      ) : (
                        <div>
                          <div className="mb-3">
                            <div className="d-flex align-items-center mb-2">
                              <Mail size={16} className="text-muted me-2" />
                              <span>{userInfo.email}</span>
                            </div>
                            <div className="d-flex align-items-center mb-2">
                              <Phone size={16} className="text-muted me-2" />
                              <span>{userInfo.phone || 'Not provided'}</span>
                            </div>
                            <div className="d-flex align-items-center mb-2">
                              <MapPin size={16} className="text-muted me-2" />
                              <span>{userInfo.location || 'Not provided'}</span>
                            </div>
                            <div className="d-flex align-items-center">
                              <Calendar size={16} className="text-muted me-2" />
                              <span>{userInfo.dateOfBirth ? new Date(userInfo.dateOfBirth).toLocaleDateString() : 'Not provided'}</span>
                            </div>
                          </div>
                        </div>
                      )}
                    </div>
                  </div>
                </div>

                <div className="col-12 col-md-6 col-lg-4 mb-4">
                  <div className="card shadow-sm h-100">
                    <div className="card-header">
                      <h5 className="mb-0">Academic Information</h5>
                    </div>
                    <div className="card-body">
                      {isEditing ? (
                        <div>
                          <label className="form-label">Student ID</label>
                          <input
                            type="text"
                            className="form-control mb-3"
                            value={userInfo.studentId}
                            onChange={(e) => handleInputChange("studentId", e.target.value)}
                            placeholder="Enter student ID"
                          />
                          <label className="form-label">Academic Year</label>
                          <select
                            className="form-select"
                            value={userInfo.year}
                            onChange={(e) => handleInputChange("year", e.target.value)}
                          >
                            <option value="Freshman">Freshman</option>
                            <option value="Sophomore">Sophomore</option>
                            <option value="Junior">Junior</option>
                            <option value="Senior">Senior</option>
                            <option value="Graduate">Graduate</option>
                          </select>
                        </div>
                      ) : (
                        <div>
                          <div className="mb-2">
                            <strong>Student ID:</strong> {userInfo.studentId || 'Not provided'}
                          </div>
                          <div className="mb-2">
                            <strong>Major:</strong> {userInfo.major}
                          </div>
                          <div>
                            <strong>Year:</strong> {userInfo.year}
                          </div>
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
