/* eslint-disable no-unused-vars */
import React, { useState, useEffect } from "react";
import {
  User,
  Book,
  Award,
  Edit3,
  Camera,
  Mail,
  Phone,
  MapPin,
  Calendar,
  GraduationCap,
  Trophy,
  Clock,
  BarChart3,
  Save,
  X,
  Upload,
  AlertCircle,
  CheckCircle,
  Loader,
} from "lucide-react";

export default function Profile() {
  const [activeTab, setActiveTab] = useState("overview");
  const [isEditing, setIsEditing] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [profileId, setProfileId] = useState("user123"); // This would come from authentication/props

  const [userInfo, setUserInfo] = useState({
    firstName: "",
    lastName: "",
    name: "",
    email: "",
    phone: "",
    location: "",
    dateOfBirth: "",
    bio: "",
    profileImage: "https://images.unsplash.com/photo-1494790108755-2616b612b786?w=150&h=150&fit=crop&crop=face",
    studentId: "",
    major: "",
    year: "",
    joinDate: "",
    isActive: true,
    achievements: [],
    courses: [],
    skills: []
  });

  const [originalUserInfo, setOriginalUserInfo] = useState({});

  // Fetch user profile on component mount
  useEffect(() => {
    fetchProfile();
  }, [profileId]);

  const fetchProfile = async () => {
    setIsLoading(true);
    setError(null);
    
    try {
      const response = await fetch(`/api/profiles/${profileId}`);
      const data = await response.json();
      
      if (data.success) {
        const profileData = data.data;
        setUserInfo({
          ...profileData,
          dateOfBirth: profileData.dateOfBirth ? new Date(profileData.dateOfBirth).toISOString().split('T')[0] : '',
          joinDate: profileData.formattedJoinDate || new Date(profileData.joinDate).toLocaleDateString('en-US', { 
            year: 'numeric', 
            month: 'long' 
          })
        });
        setOriginalUserInfo(profileData);
      } else {
        setError(data.message || 'Failed to fetch profile');
      }
    } catch (err) {
      setError('Network error. Please try again.');
      console.error('Error fetching profile:', err);
    } finally {
      setIsLoading(false);
    }
  };

  const handleInputChange = (field, value) => {
    setUserInfo((prev) => ({
      ...prev,
      [field]: value,
      ...(field === "firstName" || field === "lastName"
        ? {
            name:
              field === "firstName"
                ? `${value} ${prev.lastName}`
                : `${prev.firstName} ${value}`,
          }
        : {}),
    }));
  };

  const handleImageUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setIsLoading(true);
    setError(null);

    try {
      const formData = new FormData();
      formData.append('profileImage', file);

      const response = await fetch(`/api/profiles/${profileId}/upload`, {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();

      if (data.success) {
        setUserInfo(prev => ({ ...prev, profileImage: data.data.profileImage }));
        setSuccess('Profile image updated successfully!');
      } else {
        setError(data.message || 'Failed to upload image');
      }
    } catch (err) {
      setError('Failed to upload image. Please try again.');
      console.error('Error uploading image:', err);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSave = async () => {
    setIsLoading(true);
    setError(null);
    setSuccess(null);

    // Prepare data for API
    const updateData = {
      firstName: userInfo.firstName,
      lastName: userInfo.lastName,
      email: userInfo.email,
      phone: userInfo.phone,
      location: userInfo.location,
      dateOfBirth: userInfo.dateOfBirth,
      bio: userInfo.bio,
      studentId: userInfo.studentId,
      major: userInfo.major,
      year: userInfo.year,
    };

    try {
      const response = await fetch(`/api/profiles/${profileId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updateData),
      });

      const data = await response.json();

      if (data.success) {
        setSuccess('Profile updated successfully!');
        setIsEditing(false);
        setOriginalUserInfo(data.data);
        // Update joinDate format
        setUserInfo(prev => ({
          ...prev,
          joinDate: data.data.formattedJoinDate || new Date(data.data.joinDate).toLocaleDateString('en-US', { 
            year: 'numeric', 
            month: 'long' 
          })
        }));
      } else {
        setError(data.message || 'Failed to update profile');
      }
    } catch (err) {
      setError('Network error. Please try again.');
      console.error('Error updating profile:', err);
    } finally {
      setIsLoading(false);
    }
  };

  const handleCancel = () => {
    setUserInfo(originalUserInfo);
    setIsEditing(false);
    setError(null);
    setSuccess(null);
  };

  const dismissAlert = () => {
    setError(null);
    setSuccess(null);
  };

  if (isLoading && !userInfo.name) {
    return (
      <div className="container-fluid bg-light min-vh-100 py-4">
        <div className="container-sm">
          <div className="d-flex justify-content-center align-items-center" style={{ minHeight: '300px' }}>
            <div className="text-center">
              <Loader className="animate-spin mb-3" size={48} />
              <p>Loading profile...</p>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid bg-light min-vh-100 py-4">
      <div className="container-sm">
        {/* Alert Messages */}
        {error && (
          <div className="alert alert-danger alert-dismissible fade show mb-4" role="alert">
            <AlertCircle size={20} className="me-2" />
            {error}
            <button type="button" className="btn-close" onClick={dismissAlert}></button>
          </div>
        )}
        
        {success && (
          <div className="alert alert-success alert-dismissible fade show mb-4" role="alert">
            <CheckCircle size={20} className="me-2" />
            {success}
            <button type="button" className="btn-close" onClick={dismissAlert}></button>
          </div>
        )}

        <div className="row mb-4">
          <div className="col-12">
            <div className="card shadow-sm">
              <div className="card-body">
                <div className="row align-items-center">
                  <div className="col-auto">
                    <div className="position-relative">
                      <img
                        src={userInfo.profileImage}
                        alt="Profile"
                        className="rounded-circle img-fluid"
                        style={{ width: "80px", height: "80px", objectFit: "cover" }}
                      />
                      {isEditing ? (
                        <div className="position-absolute bottom-0 end-0">
                          <input
                            type="file"
                            id="profileImageUpload"
                            accept="image/*"
                            onChange={handleImageUpload}
                            className="d-none"
                          />
                          <label
                            htmlFor="profileImageUpload"
                            className="btn btn-primary btn-sm rounded-circle d-flex align-items-center justify-content-center"
                            style={{ width: "35px", height: "35px" }}
                          >
                            <Upload size={16} />
                          </label>
                        </div>
                      ) : (
                        <button className="btn btn-primary btn-sm position-absolute bottom-0 end-0 rounded-circle">
                          <Camera size={14} />
                        </button>
                      )}
                    </div>
                  </div>
                  <div className="col">
                    <div className="d-flex flex-column flex-md-row justify-content-between align-items-start gap-3">
                      <div>
                        {isEditing ? (
                          <div className="mb-3">
                            <div className="row">
                              <div className="col-md-6 mb-2">
                                <input
                                  type="text"
                                  className="form-control"
                                  value={userInfo.firstName}
                                  onChange={(e) => handleInputChange("firstName", e.target.value)}
                                  placeholder="First Name"
                                  required
                                />
                              </div>
                              <div className="col-md-6 mb-2">
                                <input
                                  type="text"
                                  className="form-control"
                                  value={userInfo.lastName}
                                  onChange={(e) => handleInputChange("lastName", e.target.value)}
                                  placeholder="Last Name"
                                  required
                                />
                              </div>
                            </div>
                          </div>
                        ) : (
                          <>
                            <h2 className="mb-1 text-break">{userInfo.name}</h2>
                            <p className="text-muted mb-2">{userInfo.major} Student</p>
                            <div className="mb-2">
                              <span className={`badge ${userInfo.isActive ? 'bg-success' : 'bg-secondary'}`}>
                                {userInfo.isActive ? 'Active' : 'Inactive'}
                              </span>
                            </div>
                          </>
                        )}
                        <div className="d-flex flex-wrap gap-3 text-sm">
                          <span><Mail size={16} className="me-1" />{userInfo.email}</span>
                          <span><MapPin size={16} className="me-1" />{userInfo.location}</span>
                          <span><Calendar size={16} className="me-1" />Joined {userInfo.joinDate}</span>
                        </div>
                      </div>
                      {isEditing ? (
                        <div className="d-flex gap-2">
                          <button 
                            className="btn btn-success" 
                            onClick={handleSave}
                            disabled={isLoading}
                          >
                            {isLoading ? <Loader size={16} className="me-1 animate-spin" /> : <Save size={16} className="me-1" />}
                            Save
                          </button>
                          <button 
                            className="btn btn-outline-secondary" 
                            onClick={handleCancel}
                            disabled={isLoading}
                          >
                            <X size={16} className="me-1" />Cancel
                          </button>
                        </div>
                      ) : (
                        <button className="btn btn-outline-primary" onClick={() => setIsEditing(!isEditing)}>
                          <Edit3 size={16} className="me-1" />Edit Profile
                        </button>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Overview Tab */}
        <div className="row">
          <div className="col-12">
            {activeTab === "overview" && (
              <div className="row">
                <div className="col-12 col-md-6 col-lg-4 mb-4">
                  <div className="card shadow-sm h-100">
                    <div className="card-header">
                      <h5 className="mb-0">About Me</h5>
                    </div>
                    <div className="card-body">
                      {isEditing ? (
                        <textarea
                          className="form-control"
                          rows="4"
                          value={userInfo.bio}
                          onChange={(e) => handleInputChange("bio", e.target.value)}
                          placeholder="Tell us about yourself..."
                          maxLength="500"
                        />
                      ) : (
                        <p className="mb-0">{userInfo.bio || 'No bio available'}</p>
                      )}
                    </div>
                  </div>
                </div>

                <div className="col-12 col-md-6 col-lg-4 mb-4">
                  <div className="card shadow-sm h-100">
                    <div className="card-header">
                      <h5 className="mb-0">Contact Information</h5>
                    </div>
                    <div className="card-body">
                      {isEditing ? (
                        <div className="mb-3">
                          <label className="form-label">Email *</label>
                          <input
                            type="email"
                            className="form-control mb-3"
                            value={userInfo.email}
                            onChange={(e) => handleInputChange("email", e.target.value)}
                            required
                          />
                          <label className="form-label">Phone</label>
                          <input
                            type="tel"
                            className="form-control mb-3"
                            value={userInfo.phone}
                            onChange={(e) => handleInputChange("phone", e.target.value)}
                          />
                          <label className="form-label">Location</label>
                          <input
                            type="text"
                            className="form-control mb-3"
                            value={userInfo.location}
                            onChange={(e) => handleInputChange("location", e.target.value)}
                          />
                          <label className="form-label">Date of Birth *</label>
                          <input
                            type="date"
                            className="form-control"
                            value={userInfo.dateOfBirth}
                            onChange={(e) => handleInputChange("dateOfBirth", e.target.value)}
                            required
                          />
                        </div>
                      ) : (
                        <div>
                          <div className="mb-3">
                            <div className="d-flex align-items-center mb-2">
                              <Mail size={16} className="text-muted me-2" />
                              <span>{userInfo.email}</span>
                            </div>
                            <div className="d-flex align-items-center mb-2">
                              <Phone size={16} className="text-muted me-2" />
                              <span>{userInfo.phone || 'Not provided'}</span>
                            </div>
                            <div className="d-flex align-items-center mb-2">
                              <MapPin size={16} className="text-muted me-2" />
                              <span>{userInfo.location || 'Not provided'}</span>
                            </div>
                            <div className="d-flex align-items-center">
                              <Calendar size={16} className="text-muted me-2" />
                              <span>
                                {userInfo.dateOfBirth ? new Date(userInfo.dateOfBirth).toLocaleDateString() : 'Not provided'}
                                {userInfo.age && ` (${userInfo.age} years old)`}
                              </span>
                            </div>
                          </div>
                        </div>
                      )}
                    </div>
                  </div>
                </div>

                <div className="col-12 col-md-6 col-lg-4 mb-4">
                  <div className="card shadow-sm h-100">
                    <div className="card-header">
                      <h5 className="mb-0">Academic Information</h5>
                    </div>
                    <div className="card-body">
                      {isEditing ? (
                        <div>
                          <label className="form-label">Student ID *</label>
                          <input
                            type="text"
                            className="form-control mb-3"
                            value={userInfo.studentId}
                            onChange={(e) => handleInputChange("studentId", e.target.value)}
                            required
                          />
                          <label className="form-label">Major *</label>
                          <input
                            type="text"
                            className="form-control mb-3"
                            value={userInfo.major}
                            onChange={(e) => handleInputChange("major", e.target.value)}
                            required
                          />
                          <label className="form-label">Academic Year *</label>
                          <select
                            className="form-select"
                            value={userInfo.year}
                            onChange={(e) => handleInputChange("year", e.target.value)}
                            required
                          >
                            <option value="">Select Year</option>
                            <option value="Freshman">Freshman</option>
                            <option value="Sophomore">Sophomore</option>
                            <option value="Junior">Junior</option>
                            <option value="Senior">Senior</option>
                            <option value="Graduate">Graduate</option>
                          </select>
                        </div>
                      ) : (
                        <div>
                          <div className="mb-2">
                            <strong>Student ID:</strong> {userInfo.studentId}
                          </div>
                          <div className="mb-2">
                            <strong>Major:</strong> {userInfo.major}
                          </div>
                          <div>
                            <strong>Year:</strong> {userInfo.year}
                          </div>
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
