// src/components/CourseCard.jsx
import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./CourseCard.css";

function CourseCard({ course, onFavouriteToggle, showToast }) {
  const [isFavorite, setIsFavorite] = useState(false);
  const navigate = useNavigate();

  // Get the course ID - handle both MongoDB _id and regular id
  const getCourseId = () => {
    return course._id || course.id;
  };

  // Check if user is authenticated
  const isUserAuthenticated = () => {
    // Check for auth token in localStorage
    const token = localStorage.getItem('authToken') || localStorage.getItem('token');
    
    // You can also check for user data
    const userData = localStorage.getItem('userData') || localStorage.getItem('user');
    
    return !!(token || userData);
  };

  useEffect(() => {
    // Check if course is in favorites on component mount (only if user is authenticated)
    const checkFavoriteStatus = () => {
      try {
        if (!isUserAuthenticated()) {
          setIsFavorite(false);
          return;
        }

        const courseId = getCourseId();
        if (!courseId) return; // Exit if no valid ID

        const favorites = JSON.parse(localStorage.getItem('userFavourites') || '[]');
        setIsFavorite(favorites.includes(courseId));
      } catch (error) {
        console.error('Error checking favorite status:', error);
      }
    };

    checkFavoriteStatus();
  }, [course._id, course.id]); // Watch both possible ID properties

  const handleFavoriteToggle = (e) => {
    e.preventDefault(); // Prevent any parent click events
    e.stopPropagation();

    // Check if user is authenticated first
    if (!isUserAuthenticated()) {
      // Show toast notification about needing to login
      if (showToast) {
        showToast('Please login to add courses to favorites', 'info');
      }
      
      // Redirect to login page after a short delay
      setTimeout(() => {
        navigate('/login'); // Change to '/signup' if you prefer signup page
      }, 1500);
      
      return;
    }

    try {
      const courseId = getCourseId();
      if (!courseId) {
        console.error('No valid course ID found');
        if (showToast) {
          showToast('Error: Course ID not found', 'error');
        }
        return;
      }

      const favorites = JSON.parse(localStorage.getItem('userFavourites') || '[]');
      let updatedFavorites;
      let message;

      if (isFavorite) {
        // Remove from favorites
        updatedFavorites = favorites.filter(id => id !== courseId);
        message = 'Removed from favorites';
        setIsFavorite(false);
      } else {
        // Add to favorites
        updatedFavorites = [...favorites, courseId];
        message = 'Added to favorites';
        setIsFavorite(true);
      }

      localStorage.setItem('userFavourites', JSON.stringify(updatedFavorites));

      // Show toast notification if showToast is provided
      if (showToast) {
        showToast(message, 'success');
      }

      // Call parent callback if provided
      if (onFavouriteToggle) {
        onFavouriteToggle(courseId, !isFavorite);
      }
    } catch (error) {
      console.error('Error toggling favorite:', error);
      if (showToast) {
        showToast('Error updating favorites', 'error');
      }
    }
  };

  // Handle image URL - use thumbnailUrl from backend if available, otherwise fallback to image
  const getImageUrl = () => {
    if (course.thumbnailUrl) {
      return `http://localhost:5000${course.thumbnailUrl}`;
    }
    return course.image || '/default-course-image.jpg';
  };

  // Debug: Log course data to help identify the issue
  console.log('Course data:', course);

  const courseId = getCourseId();

  // If no valid course ID, show error state
  if (!courseId) {
    console.error('Course missing ID:', course);
    return (
      <div className="card shadow-sm course-card">
        <div className="card-body">
          <h5 className="card-title text-danger">Error: Invalid Course Data</h5>
          <p className="card-text">Course ID is missing or invalid.</p>
        </div>
      </div>
    );
  }

  const isAuthenticated = isUserAuthenticated();

  return (
    <div className="card shadow-sm course-card">
      <div className="course-image-wrapper">
        <img
          src={getImageUrl()}
          className="card-img-top"
          alt={course.title}
          style={{ height: "180px", objectFit: "cover" }}
        />
      </div>
      <div className="card-body d-flex flex-column">
        <h5 className="card-title">{course.title?.slice(0, 50) || "Untitled Course"}</h5>
        <p className="card-text">
          {course.description?.slice(0, 100) || "No description available"}...
        </p>

        {/* Show instructor if available from backend */}
        {course.instructor && (
          <p className="text-muted mb-2">
            <strong>Instructor:</strong> {course.instructor}
          </p>
        )}

        <p className="text-success mb-3">
          {course.isFree ? "Free" : `₹${course.price || 0}`}
        </p>

        <div className="mt-auto position-relative">
          <Link to={`/courses/${courseId}`} className="btn btn-primary w-100">
            View Course
          </Link>
          {/* Favorite Button */}
          <button
            className={`favorite-btn ${isFavorite ? 'favorite-active' : ''} ${!isAuthenticated ? 'favorite-disabled' : ''}`}
            onClick={handleFavoriteToggle}
            title={
              !isAuthenticated 
                ? 'Login to add to favorites' 
                : isFavorite 
                  ? 'Remove from favorites' 
                  : 'Add to favorites'
            }
            aria-label={
              !isAuthenticated 
                ? 'Login to add to favorites' 
                : isFavorite 
                  ? 'Remove from favorites' 
                  : 'Add to favorites'
            }
          >
            <i className={`bi ${isFavorite ? 'bi-heart-fill' : 'bi-heart'}`}></i>
          </button>
        </div>
      </div>
    </div>
  );
}

export default CourseCard;
