import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

function CourseDetail() {
    const { id } = useParams();
    const navigate = useNavigate();
    const [course, setCourse] = useState(null);
    const [loading, setLoading] = useState(true);
    const [addingToCart, setAddingToCart] = useState(false);

    useEffect(() => {
        const fetchCourse = async () => {
            try {
                const res = await axios.get(`http://localhost:5000/api/admin/course/${id}`);
                setCourse(res.data);
            } catch (err) {
                console.error("Error fetching course:", err);
            } finally {
                setLoading(false);
            }
        };

        fetchCourse();
    }, [id]);

    const handleAddToCart = async () => {
        if (addingToCart) return; // Prevent double clicks

        try {
            setAddingToCart(true);
            const token = localStorage.getItem("token");

            // Debug logs
            console.log("=== ADD TO CART DEBUG ===");
            console.log("Token exists:", !!token);
            console.log("Token length:", token?.length);
            console.log("Token preview:", token?.substring(0, 50) + "...");
            console.log("Course ID:", course._id);
            console.log("API URL:", process.env.REACT_APP_API_URL);

            // Decode JWT to see contents (without verification)
            try {
                const tokenParts = token.split('.');
                const payload = JSON.parse(atob(tokenParts[1]));
                console.log("Token payload:", payload);
                console.log("Token expires:", new Date(payload.exp * 1000));
                console.log("Token issued:", new Date(payload.iat * 1000));
            } catch (e) {
                console.log("Could not decode token:", e);
            }

            if (!token) {
                console.log("❌ No token found, redirecting to login");
                navigate("/login");
                return;
            }

            console.log("✅ Token found, making API call...");

            const response = await axios.post(
                `${process.env.REACT_APP_API_URL}/api/cart`,
                { courseId: course._id },
                {
                    headers: {
                        Authorization: `Bearer ${token}`,
                        "Content-Type": "application/json"
                    }
                }
            );

            console.log("✅ API call successful:", response.data);

            // ✅ FIXED: Check for success message instead of success field
            if (response.data.message === 'Course added to cart' || response.status === 200) {
                alert("Course added to cart!");
            }
        } catch (err) {
            console.error("=== CART ERROR DEBUG ===");
            console.error("Error status:", err.response?.status);
            console.error("Error message:", err.response?.data?.message);
            console.error("Full error response:", err.response?.data);
            console.error("Full error object:", err);

            if (err.response?.status === 401) {
                console.log("❌ 401 Unauthorized - Token might be expired/invalid");
                console.log("Removing token and redirecting to login");

                // Temporary: Ask user before redirecting
                const shouldRedirect = window.confirm("Your session has expired. Redirect to login?");
                if (shouldRedirect) {
                    localStorage.removeItem("token");
                    navigate("/login");
                } else {
                    alert("Token is invalid. Please refresh and try logging in again.");
                }
            } else if (err.response?.status === 409) {
                // ✅ Handle "already in cart" case with a friendly message
                alert("This course is already in your cart!");
            } else {
                alert(err.response?.data?.message || "Failed to add to cart");
            }
        } finally {
            setAddingToCart(false);
        }
    };

    const handleBuyNow = () => {
        const token = localStorage.getItem("token");
        if (!token) return navigate("/login");

        navigate(`/checkout/${course._id}`);
    };

    if (loading) return <h3 className="text-center mt-5">Loading course...</h3>;
    if (!course) return <h3 className="text-center mt-5 text-danger">Course not found!</h3>;

    return (
        <div className="container mt-5">
            <h2 className="mb-3">{course.title}</h2>
            <img
                src={`http://localhost:5000${course.thumbnailUrl}`}
                alt={course.title}
                className="img-fluid mb-4"
                style={{ maxHeight: "400px", objectFit: "cover", borderRadius: "10px" }}
            />

            <p><strong>Instructor:</strong> {course.instructor || "N/A"}</p>
            <p><strong>Category:</strong> {course.category || "N/A"}</p>
            <p><strong>Duration:</strong> {course.duration || "N/A"}</p>
            <p className="mt-3">{course.description}</p>
            <p><strong>Price:</strong> {course.price === 0 ? "Free" : `₹${course.price}`}</p>

            {course.price > 0 && (
                <div className="mt-3 d-flex gap-2">
                    <button
                        className="btn btn-primary"
                        onClick={handleAddToCart}
                        disabled={addingToCart}
                    >
                        {addingToCart ? "Adding..." : "Add to Cart"}
                    </button>
                    <button className="btn btn-success" onClick={handleBuyNow}>
                        Buy Now
                    </button>
                </div>
            )}
        </div>
    );
}

export default CourseDetail;
